*/ // DEFINE PINS AND PINREGISTERS We use pins and pinregisters for speed #define P_M1_PWM 9    // Note if you change those you need also change the PCINT #define P_M2_PWM 10 #define P_M1_IN1 8 #define P_M2_IN1 12 #define P_M1_IN2 11 #define P_M2_IN2 13 #define P_M1_QE1 7 #define P_M1_QE2 6 #define P_M2_QE1 5 #define P_M2_QE2 4 #define P_M1_LS1 3 #define P_M1_LS2 2 #define P_M2_LS1 A2 #define P_M2_LS2 A3 #define P_M1_CS A0 #define P_M2_CS A1

//flags #define F_ERROR 0       // 0 no error 1 is error #define F_ENABLE_LS1 1  // 1 =  use limit switches use limitswitch ls1 tied to in1 (fwd) #define F_ENABLE_LS2 2  // 1 =  ls2 tied to in2(rev) #define F_LS_OPEN 3     // Limit switch unactivated state either 0 or 1 depending on NO, NC and if tied to ground or VCC #define F_LS1 4         // Current limit switch values #define F_LS2 5 #define F_ENCODER 6     // Encoder is present or not #define F_QUADRATURE 7  // Encoder if present is Quadrature (immaterial if F_ENCODER = 0 #define F_REVERSE 8     // Reverse the motor (0 - speed input) if 1 but leaves LS assignments alone #define F_DIRECTION 9   // current direction reflection of in1(FWD) pin. Can be used to guess direction if not quadrature encoder #define F_Brake 10      // Motor in Brake mode #define F_COAST 11      // Motor in Coast mode (disabled) #define F_ENABLE_CS 12  // Use Current control (limit to max current) #define F_CS_NOW 13     // measure current sense now

// state register settings = bit positions corresponding with input bits that cause an interrupt like encoder and ls

class BTS7960_D { public: BTS7960_D(uint8_t motor); void Start(); // needs to be run in setup before setting anything else - makes sure the timer1 is properly set void Enable_CS(); void Disable_CS(); void GetLS(); void Set_ls(uint8_t ls1, uint8_t ls2, uint8_t open); // 0 = off, 1 = on, open shows if ls is inactive if you see a 0 or a 1 void Brake(); void Coast(); bool Drive(int16_t mspeed); uint16_t getStatus(); //Returns flags void Reverse(); //sets reverse flag void Update(); // needs to be called periodically to check thingslike current, switches, encoders etc etc void CheckCS(); //

private: uint8_t _motor = 0; // pins uint8_t _pwm;    // pin for pw uint8_t _in1;    // pin for L_EN which is connecte internally to in_1 uint8_t _in2;    // pinf for R_EN int8_t _cs = -1;    // Current sense 1 pin must be analog pin int8_t _ls1 = -1;    // Limit switch in the in1 direction int8_t _ls2 = -1;    // Limit swithc in the in2 direction int8_t _enc1 = -1;  //encoder input 1 int8_t _enc2 = -1;   //encoder input 2 }; /* Constructor do basic rudimentary setup for now lets handle the pins hard coded and for now define it for UNO/Nano F_Error for now to detect if we called with an invalid Motor. / BTS7960_D::BTS7960_D(uint8_t motor) { if (motor < 1 || motor > 2) { bitWrite(flags, F_ERROR, 1); return; // only allow motor 1 and 2 on uno/nano } bitWrite(flags, F_ERROR, 0); _motor = motor; _pwm = (motor == 1) ? P_M1_PWM : P_M2_PWM; _in1 = (motor == 1) ? P_M1_IN1 : P_M2_IN1; _in2 = (motor == 1) ? P_M1_IN2 : P_M2_IN2; pinMode(_pwm, OUTPUT); pinMode(_in1, OUTPUT); pinMode(_in2, OUTPUT); digitalWrite(_pwm, 0); digitalWrite(_in1, 0);  //  make sure everything is turned off digitalWrite(_in2, 0); //Setup pin interrupt for now we only accept them on PCINT 1 pin 9, 10 in the future we can expand here } void BTS7960_D::Start() { cli(); //WGM 10 and 12 set FASTPMW 8 Bit to get them both to rise simultaneously and //CS 11 gives us with that an 8kHz cycle TCCR1B = 0B00001010;  // sets CS11 and WGM12 TCCR1A = 0B00000001;  // Sets WGM10 PCICR |= 0b00000001; // Enables Ports B Pin Change Interrupts PCMSK0 |= (_motor == 10) ? 0b00000010 : 0b00000100; // PCINT1 or 2 - pin 9 and 10 TIMSK1 != 0b00000110; // set mask for both sei(); } // read Limit switches and set position if the pin = -1 (disabled) switch is set to 0 void BTS7960_D::GetLS () { if (_ls1 >= 0) { if (digitalRead(_ls1)) { bitSet(flags, F_LS1); } else { bitClear(flags, F_LS1); } } else { bitClear(flags, F_LS1); } if (_ls2 >= 0) { if (digitalRead(_ls2)) { bitSet(flags, F_LS2); } else { bitClear(flags, F_LS2); } } else { bitClear(flags, F_LS2); } } // see if its time to read the CS note that it will read aprox. every 10 ms or longer depending on how busy the system is void BTS7960_D::CheckCS() { uint32_t m = millis(); if (m < 10 || m - 10 > last_cs_read) { bitSet(flags, F_CS_NOW); } } void BTS7960_D::Update() { //if we hit the limitswitch and the limitswitch is active then stop the motor //  if (bitRead(flags, F_ENABLE_LS1) == 1) { //    if (bitRead(flags, F_LS_OPEN) != digitalRead(_ls1)) { //      this->Brake(); //    } //  } } //reverse motor direction void BTS7960_D::Reverse() { bitWrite(flags, F_REVERSE, 1); } / Use currentcontrol / void BTS7960_D::Enable_CS() { _cs = (_motor == 1) ? P_M1_CS : P_M2_CS; bitWrite(flags, F_ENABLE_CS, 1); bitWrite(flags, F_CS_NOW, 1); last_cs = 0; } / Disable Current Sense */ void BTS7960_D::Disable_CS() { bitWrite(flags, F_ENABLE_CS, 0); bitWrite(flags, F_CS_NOW, 0); } void BTS7960_D::Set_ls(uint8_t ls1, uint8_t ls2, uint8_t open) {

} /* get statusflags / uint16_t BTS7960_D::getStatus() { return flags; } / Put motor in coast by disabling the unit / void BTS7960_D::Coast() { analogWrite(_pwm, 0); // will set the pin to 0 last_Speed = curr_Speed; curr_Speed = 0; } / Put the motor in Brake by turning both lower ends on and the upper ones off */ void BTS7960_D::Brake() { analogWrite(_pwm, 255); // will set the pin to 1 digitalWrite(_in1, 0); digitalWrite(_in2, 0); last_Speed = curr_Speed; curr_Speed = 0; } //Drive the motor positive = FWD negative = reverse bool BTS7960_D::Drive(int16_t mspeed) { if (bitRead(flags, F_REVERSE) != 0) { mspeed = 0 - mspeed; // reverse it } if (mspeed == 0) { analogWrite(_pwm, mspeed); // leave flags alone } else { if (mspeed > 0) // forward { if (bitRead(flags, F_DIRECTION) != 1) { this->Brake(); } bitWrite(flags, F_DIRECTION, 1); //Set direction digitalWrite(_in1, 1); digitalWrite(_in2, 0); mspeed = (mspeed < 10) ? 10 : (mspeed > 245) ? 245 : mspeed; analogWrite(_pwm, mspeed); } else { if (bitRead(flags, F_DIRECTION) != 0) { this->Brake(); } bitWrite(flags, F_DIRECTION, 0); digitalWrite(_in1, 0); digitalWrite(_in2, 1); mspeed = (mspeed > -10) ? -10 : (mspeed < -245) ? -245 : mspeed; analogWrite(_pwm, abs(mspeed)); } } last_Speed = curr_Speed; curr_Speed = mspeed; return true; }

