/* On the wire there are always a set of  bytes transmiteed byte 1 = number of bytes in transmission after this  1-255 byte 2 = Command 1-255 - needs at least 1 command byte 0 would be NOP can be used as a heartbeat and will require a 0 back byte 3-n parameters of command Replies with either the required # of bytes or 0 = ok 1 = error If it receives a command 1 (error) then it will reset the communication and expect a command next sending multiple 1 is ok */ #define WIRE_ID 1 // wire id of this device - change if you use multiple in a system

#define WIRE_BUFF 4     // # of bytes in data buffer uint8_t wire_state = 0; // 0 = waiting for transmission 1 = count 2 cmd received 3 = parameters resets to 0 at end uint8_t wire_wcnt = 0; // wait count of bytes still to be received currently limit to up to 4 uint8_t curr_cmd = 0; uint8_t wbuff[WIRE_BUFF];

void clear_wbuff() { for (uint8_t i = 0; i < WIRE_BUFF; i++) { wbuff[i] = 0; } }

void receiveEvent(int howMany) { while (0 < Wire.available()) // loop through all but the last { uint8_t c = Wire.read(); // receive byte as a character switch (wire_state) { case 0: // byte is count wire_wcnt = c; wire_state++; break; case 1 : // command wire_state = (–wire_wcnt == 0) ? 0 : wire_state++; curr_cmd = c;

} }

