public class Tower extends SubsystemBase {

private final NetworkTableEntry towerUpperMotorEntry, towerLowerMotorEntry, towerLowerSensorEntry, towerUpperSensorEntry;

private final CANSparkMax lowerMotor = new CANSparkMax(Constants.LOWER_TOWER_MOTOR,MotorType.kBrushless);

private final CANSparkMax upperMotor = new CANSparkMax(Constants.UPPER_TOWER_MOTOR,MotorType.kBrushless);

private DigitalInput lowerSensor = new DigitalInput(Constants.LOWER_SENSOR_PORT_INPUT);

private DigitalInput upperSensor = new DigitalInput(Constants.UPPER_SENSOR_PORT_INPUT);

public Tower() {

}

/** Sets the upper motor speed for the tower. */ public void setSpeedUpper(double speed) { upperMotor.set(speed); }

/** Returns the set speed of the upper motor. */ public double getSpeedUpper() { return upperMotor.get(); }

/** Sets the speed of the lower tower motor. */ public void setSpeedLower(double speed) { lowerMotor.set(speed); }

/** Returns the set speed of the lower motor */ public double getSpeedLower() { return lowerMotor.get(); }

public boolean isBallHereUpper() { return upperSensor.get(); }

public boolean isBallHereLower() { return lowerSensor.get(); }

public void stop() { lowerMotor.set(0); upperMotor.set(0); }

@Override public void periodic() { towerUpperMotorEntry.setDouble(getSpeedUpper()); towerLowerMotorEntry.setDouble(getSpeedLower());

// This does not correctly show the status of the beam break towerUpperSensorEntry.setBoolean(isBallHereUpper()); towerLowerSensorEntry.setBoolean(isBallHereLower()); }

}

