package frc.robot.subsystems.drive;

import com.ctre.phoenix6.BaseStatusSignal; import com.ctre.phoenix6.StatusSignal; import com.ctre.phoenix6.configs.CANcoderConfiguration; import com.ctre.phoenix6.hardware.CANcoder; import com.revrobotics.CANSparkBase.IdleMode; import com.revrobotics.CANSparkLowLevel.MotorType; import com.revrobotics.CANSparkMax; import com.revrobotics.RelativeEncoder; import edu.wpi.first.math.geometry.Rotation2d; import edu.wpi.first.math.util.Units;

/**

To calibrate the absolute encoder offsets, point the modules straight (such that forward motion

on the drive motor will propel the robot forward) and copy the reported values from the absolute

encoders using AdvantageScope. These values are logged under

“/Drive/ModuleX/TurnAbsolutePositionRad” */ public class ModuleIOSparkMax implements ModuleIO { // Gear ratios for SDS MK4i L3, adjust as necessary private static final double DRIVE_GEAR_RATIO = (50.0 / 14.0) * (16.0 / 28.0) * (45.0 / 15.0); private static final double TURN_GEAR_RATIO = 150 / 7;

private final CANSparkMax driveSparkMax; private final CANSparkMax turnSparkMax;

private final RelativeEncoder driveEncoder; private final RelativeEncoder turnRelativeEncoder; private final CANcoder cancoder; private final Rotation2d absoluteEncoderOffset; private final StatusSignal turnAbsolutePosition;

private final boolean isTurnMotorInverted = true;

public ModuleIOSparkMax(int index) { switch (index) { case 0: driveSparkMax = new CANSparkMax(1, MotorType.kBrushless); turnSparkMax = new CANSparkMax(2, MotorType.kBrushless); cancoder = new CANcoder(9); absoluteEncoderOffset = new Rotation2d(2.269); // MUST BE CALIBRATED

}

@Override public void updateInputs(ModuleIOInputs inputs) {

}

@Override public void setDriveVoltage(double volts) { driveSparkMax.setVoltage(volts); }

@Override public void setTurnVoltage(double volts) { turnSparkMax.setVoltage(volts); }

@Override public void setDriveBrakeMode(boolean enable) { driveSparkMax.setIdleMode(enable ? IdleMode.kBrake : IdleMode.kCoast); }

@Override public void setTurnBrakeMode(boolean enable) { turnSparkMax.setIdleMode(enable ? IdleMode.kBrake : IdleMode.kCoast); } }
