<p>import edu.wpi.first.math.controller.PIDController;<br>
import edu.wpi.first.math.geometry.Translation2d;<br>
import edu.wpi.first.wpilibj2.command.Command;<br>
import frc.robot.RobotContainer;<br>
import frc.robot.subsystems.Limelight;<br>
import frc.robot.subsystems.SwerveSubsystem;</p>
<p>public class AlingToTarget extends Command{</p>
<pre><code>Limelight limelight;
SwerveSubsystem subsystem;
PIDController controller = new PIDController(0, 0, 0);
RobotContainer container = new RobotContainer();
Translation2d translation = new Translation2d(0, 0);

public AlingToTarget(Limelight limelight, SwerveSubsystem subsystem){
    this.limelight = limelight;
    this.subsystem = subsystem;
    addRequirements(limelight, subsystem);
}
public void initialize(){
    controller.reset();
    controller.setSetpoint(0);
}
public void execute(){
    if(limelight.getHasTarget()){
        if(limelight.getTx() &gt; 0){
            if(container.controleXbox.getLeftBumperButton()){
                double tx = limelight.getTx();
                double correcao = controller.calculate(0,tx);

                subsystem.drive(translation, correcao, true);
            }
        }
        if(limelight.getTx() &lt; 0){
            if(container.controleXbox.getRightBumperButton()){
                double tx = limelight.getTx();
                double correcao = controller.calculate(0, tx);

                subsystem.drive(translation, correcao, true);
            }
        }
    }
}
public boolean finish(){
    return controller.atSetpoint();
}
public void end(boolean parar){
    subsystem.drive(translation, 0, true);
}
</code></pre>
