<p><em>public class ExampleSubsystem extends SubsystemBase {</em><br>
private final ExampleSubsystemIO io;<br>
private final ExampleSubsystemIOInputsAutoLogged inputs;</p>
<pre><code>public ExampleSubsystem(ExampleSubsystemIO exampleSubsystemIO) {
    this.io = exampleSubsystemIO;
    inputs = new ExampleSubsystemIOInputsAutoLogged();
}

public Command setPrecentageOutput(double power) {
    return new RunCommand(() -&gt; io.setPrecentageOutput(power), this);
}

public Command setPrecentageOutput(DoubleSupplier power) {
    return new RunCommand(() -&gt; io.setPrecentageOutput(power.getAsDouble()), this);
}

public Command setVoltageOutput(double volts) {
    return new InstantCommand(() -&gt; io.setVoltageOutput(volts), this);
}

public Command defaultCommand() {
    return setPrecentageOutput(0);
}

@Override
public void periodic() {
    io.updateInputs(inputs);
    Logger.processInputs("Example Subsystem", inputs);
    Logger.recordOutput("Power", inputs.power);

    System.out.println(inputs.power);
}
