#include &lt;Servo.h&gt;
#include &lt;LiquidCrystal_I2C.h&gt;

//User configuration:

int pinsMot1[] = {3, 4}; //the signal output pins (as many as you'd like)
int pinsMot2[] = {5, 6};
int both =8 ;
int same =9 ;
int mot1 =10 ;
int mot2 =11 ;
int PWMvalue1 ;
  int PWMvalue2 ;
const int arraySize = sizeof(pinsMot1)/sizeof(int);
Servo controllers1[arraySize];
Servo controllers2[arraySize];
LiquidCrystal_I2C lcd(0x27,16,2);

void setup() {
  Serial.begin(9600);
  pinMode(both, INPUT_PULLUP);
  pinMode(same, INPUT_PULLUP);
  pinMode(mot1, INPUT_PULLUP);
  pinMode(mot2, INPUT_PULLUP);
  for (int i=0; i&lt;arraySize; i++){
  controllers1[i].attach(pinsMot1[i]); //associate the object to a pin
  controllers2[i].attach(pinsMot2[i]); //associate the object to a pin
}
  delay(1000);
  lcd.init();
   lcd.backlight();
}


void loop() {

  int PWMvalue1 = map(analogRead(A0),0,1023,1000,2000);
  int PWMvalue2 = map(analogRead(A1),0,1023,1000,2000);

if(!digitalRead(both) &amp;&amp; !digitalRead(same)){    // runnig togethe same direction
         lcd.setCursor(0,0);
        lcd.print("MOTOR 1 :");
        lcd.setCursor(10,0);
        lcd.print("    ");
        lcd.setCursor(10,0);
        lcd.print(-(PWMvalue1/5-300));
        lcd.setCursor(14,0);
        lcd.print("%");

        lcd.setCursor(0,1);
        lcd.print("MOTOR 2 :");
        lcd.setCursor(10,1);
        lcd.print("    ");
        lcd.setCursor(10,1);
        lcd.print(-(PWMvalue1/5-300));
        lcd.setCursor(14,1);
        lcd.print("%"); 

  if(!digitalRead(mot1)) {
       for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(PWMvalue1);
        controllers2[i].writeMicroseconds(PWMvalue1);
      }

  }
  else{                                     // off togethe
      for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(1500);
        controllers2[i].writeMicroseconds(1500);
      }
  }
}

if(!digitalRead(both) &amp;&amp; digitalRead(same)){       // runnig togethe opposite direction
          lcd.setCursor(0,0);
        lcd.print("MOTOR 1 :");
        lcd.setCursor(10,0);
        lcd.print("    ");
        lcd.setCursor(10,0);
        lcd.print(-(PWMvalue1/5-300));
        lcd.setCursor(14,0);
        lcd.print("%");

        lcd.setCursor(0,1);
        lcd.print("MOTOR 2 :");
        lcd.setCursor(10,1);
        lcd.print("    ");
        lcd.setCursor(10,1);
        lcd.print((PWMvalue1/5-300));
        lcd.setCursor(14,1);
        lcd.print("%");
  if(!digitalRead(mot1)) {
       for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(PWMvalue1);
        controllers2[i].writeMicroseconds(3000-PWMvalue1);
      }

  }
  else{                                               // off togethe
      for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(1500);
        controllers2[i].writeMicroseconds(1500);
      }
  }
}

if(digitalRead(both)){       // runnig togethe opposite direction
         lcd.setCursor(0,0);
        lcd.print("MOTOR 1 :");
        lcd.setCursor(10,0);
        lcd.print("    ");
        lcd.setCursor(10,0);
        lcd.print(-(PWMvalue1/5-300));
        lcd.setCursor(14,0);
        lcd.print("%");

        lcd.setCursor(0,1);
        lcd.print("MOTOR 2 :");
        lcd.setCursor(10,1);
        lcd.print("    ");
        lcd.setCursor(10,1);
        lcd.print(-(PWMvalue2/5-300));
        lcd.setCursor(14,1);
        lcd.print("%");
  if(!digitalRead(mot1)) {
       for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(PWMvalue1);
       // controllers2[i].writeMicroseconds(PWMvalue2);
      }

  }
  else{                                               // off togethe
      for (int i=0; i&lt;arraySize; i++){
        controllers1[i].writeMicroseconds(1500);
        //controllers2[i].writeMicroseconds(1500);
      }
  }
    if(!digitalRead(mot2)) {
       for (int i=0; i&lt;arraySize; i++){
       // controllers1[i].writeMicroseconds(PWMvalue1);
        controllers2[i].writeMicroseconds(PWMvalue2);
      }
        lcd.setCursor(0,0);
        lcd.print("MOTOR 1 :");
        lcd.setCursor(10,0);
        lcd.print("    ");
        lcd.setCursor(10,0);
        lcd.print(-(PWMvalue1/5-300));
        lcd.setCursor(14,0);
        lcd.print("%");

        lcd.setCursor(0,1);
        lcd.print("MOTOR 2 :");
        lcd.setCursor(10,1);
        lcd.print("    ");
        lcd.setCursor(10,1);
        lcd.print(-(PWMvalue2/5-300));
        lcd.setCursor(14,1);
        lcd.print("%"); 
  }
  else{                                               // off togethe
      for (int i=0; i&lt;arraySize; i++){
       // controllers1[i].writeMicroseconds(1500);
        controllers2[i].writeMicroseconds(1500);
      }
  }
}


}


