<pre><code class="lang-auto">#include &lt;FastLED.h&gt;

// How many leds in your strip?
#define NUM_LEDS 150


#define LED_PIN 4
#define BUTTON_PIN 5

// Define the array of leds
CRGB leds[NUM_LEDS];

void setup () {
  pinMode(BUTTON_PIN, INPUT_PULLUP);
  FastLED.addLeds&lt;WS2812B, LED_PIN, GRB&gt;(leds, NUM_LEDS);
  FastLED.setBrightness(50);
  delay(20);
   for (int i = 0; i &lt; NUM_LEDS; i++) {
   leds[i] = CRGB::White;
   FastLED.show();
   delay(5);
   leds[i] = CRGB::Black;   
 }

  }
 void loop () {
  static long millis_start = 0;
  long millis_now = millis();
  // Assume we want to flash each group 5 times
  long k_flashCount = 5;
  long k_flashOnMillis = 50;
  long k_flashOffMillis = 50;
  long k_numGroups = 5;
  long flashPeriod = k_flashCount * (k_flashOnMillis + k_flashOffMillis);
  long totalTime = flashPeriod * k_numGroups;
  int buttonState = digitalRead(BUTTON_PIN);

  // If the button is pushed, start the sequence over
  if (buttonState == HIGH) {
    millis_start = millis_now;
  }

  // Initialize them all to black first
  for (int i = 0; i &lt; NUM_LEDS; i++) {
   leds[i] = CRGB::Black;      
  } 
  // Now, set to white any group that should be.
  long flashDelta = millis_now - millis_start;  // Where in the overall sequence are we (0 = button just pushed..)
  long flashGroup = flashDelta / flashPeriod;  // Which group to flash (first group is 0)
  bool flashOn = flashDelta % flashPeriod &lt; k_flashOnMillis;
  int countOfLEDsInGroup[5] = {10, 20, 30, 40, 50};
  int startLED = 0;
  for (int i = 0; i &lt; flashGroup; i++) {
    startLED += countOfLEDsInGroup[i];
  }
  if (flashDelta &lt; totalTime &amp;&amp; flashOn) {
    for (int l = 0; l &lt; countOfLEDsInGroup[flashGroup]; l++) {
      leds[l + startLED] = CRGB::White; 
    }
  }
  FastLED.show();
 }
