<pre><code class="lang-auto">if (VisionConfig.isPhotonVisionMode) {
      var result = photonCam_1.getLatestResult();
      photon1HasTargets = result.hasTargets();
      if (photon1HasTargets) {
        var update = photonPoseEstimator.update();
        Pose3d currentPose3d = update.get().estimatedPose;
        botPose = currentPose3d.toPose2d();
        photonTimestamp = update.get().timestampSeconds;
      }
    }
</code></pre>","post_number":3,"post_type":1,"posts_count":5,"updated_at":"2024-01-22T17:59:47.047Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":2,"reads":144,"readers_count":143,"score":53.8,"yours":false,"topic_id":451360,"topic_slug":"apriltag-odometry-with-photonvision-help","display_username":"L Sao","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","bookmarked":false,"actions_summary":[{"id":2,"count":1}],"moderator":false,"admin":false,"staff":false,"user_id":54149,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"Project Manager"},"post_url":"/t/apriltag-odometry-with-photonvision-help/451360/3","custom_user_title":"Project Manager","reactions":[{"id":"heart","type":"emoji","count":1}],"current_user_reaction":null,"reaction_users_count":1,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3293158,"name":"icemannie","username":"icemannie","avatar_template":"/letter_avatar_proxy/v4/letter/i/bc79bd/{size}.png","created_at":"2024-01-24T14:42:29.721Z","cooked":"<p>Optional &lt;EstimatedRobotPose<code>&gt;</code><br>
