package frc.robot;

import edu.wpi.first.math.controller.PIDController;
import edu.wpi.first.math.controller.ProfiledPIDController;
import edu.wpi.first.math.geometry.Pose2d;
import edu.wpi.first.math.geometry.Rotation2d;
import edu.wpi.first.math.geometry.Translation2d;
import edu.wpi.first.math.trajectory.Trajectory;
import edu.wpi.first.math.trajectory.TrajectoryConfig;
import edu.wpi.first.math.trajectory.TrajectoryGenerator;
import edu.wpi.first.wpilibj.Joystick;
import edu.wpi.first.wpilibj.XboxController;
import edu.wpi.first.wpilibj2.command.Command;
import edu.wpi.first.wpilibj2.command.InstantCommand;
import edu.wpi.first.wpilibj2.command.RunCommand;
import edu.wpi.first.wpilibj2.command.SwerveControllerCommand;
import edu.wpi.first.wpilibj2.command.button.JoystickButton;
import edu.wpi.first.wpilibj2.command.button.Trigger;
import frc.robot.Constants.AutoConstants;
import frc.robot.Constants.DriveConstants;
import frc.robot.Constants.OIConstants;
import frc.robot.subsystems.ArmSubsystem;
import frc.robot.subsystems.DriveSubsystem;
import frc.robot.subsystems.IntakeSubsystem;
import frc.robot.subsystems.LauncherSubsystem;
import frc.utils.GamepadUtils;
import java.util.List;

/*
 * This class is where the bulk of the robot should be declared.  Since Command-based is a
 * "declarative" paradigm, very little robot logic should actually be handled in the {@link Robot}
 * periodic methods (other than the scheduler calls).  Instead, the structure of the robot
 * (including subsystems, commands, and button mappings) should be declared here.
 */

public class RobotContainer {
  public int Position_Intake_Button = 7;
  public int Position_Scoring_Button = 8;
  public int Position_Home_Button = 9;
  public int Position_Moving_Button = 10;
  public int Action_Intake_Button = 5;
  public int Action_FeedLauncher_Button = 3;
  public int Action_Prefire_Button = 1;
  public int Action_RunLauncherIntake_Button = 2;

  // The robot's subsystems
  protected final DriveSubsystem m_robotDrive = new DriveSubsystem();
  protected final ArmSubsystem m_arm = new ArmSubsystem();
  protected final IntakeSubsystem m_intake = new IntakeSubsystem();
  protected final LauncherSubsystem m_launcher = new LauncherSubsystem();

  // The driver's controller
  XboxController m_driverController = new XboxController(OIConstants.kDriverControllerPort);
  Joystick exampleJoystick = new Joystick(OIConstants.kDriverControllerPort);

  /** The container for the robot. Contains subsystems, OI devices, and commands. */
  public RobotContainer() {
    // Configure the button bindings
    configureButtonBindings();

    // Configure default commands
    m_robotDrive.setDefaultCommand(
        // The left stick controls translation of the robot.
        // Turning is controlled by the X axis of the right stick.
        new RunCommand(
            () -&gt;
                m_robotDrive.drive(
                    -GamepadUtils.squareInput(
                        m_driverController.getLeftY(), OIConstants.kDriveDeadband)* (exampleJoystick.getRawAxis(4)+1)/2,
                    -GamepadUtils.squareInput(
                        m_driverController.getLeftX(), OIConstants.kDriveDeadband)* (exampleJoystick.getRawAxis(4)+1)/2,
                    -GamepadUtils.squareInput(
                        exampleJoystick.getTwist(),
                        OIConstants.kDriveDeadband) * (exampleJoystick.getRawAxis(4))/2, // was m_driverController.getRightX()
                    true,
                    false),
            m_robotDrive));

    // set the arm subsystem to run the "runAutomatic" function continuously when no other command
    // is running
    m_arm.setDefaultCommand(new RunCommand(() -&gt; m_arm.runAutomatic(), m_arm));

    // set the intake to stop (0 power) when no other command is running
    m_intake.setDefaultCommand(new RunCommand(() -&gt; m_intake.setPower(0.0), m_intake));

    // configure the launcher to stop when no other command is running
    m_launcher.setDefaultCommand(new RunCommand(() -&gt; m_launcher.stopLauncher(), m_launcher));
  }

  /**
   * Use this method to define your button-&gt;command mappings. Buttons can be created by
   * instantiating a {@link edu.wpi.first.wpilibj.GenericHID} or one of its subclasses ({@link
   * edu.wpi.first.wpilibj.Joystick} or {@link XboxController}), and then calling passing it to a
   * {@link JoystickButton}.
   */
  private void configureButtonBindings() {
    // button to put swerve modules in an "x" configuration to hold position
    new JoystickButton(m_driverController, XboxController.Button.kLeftStick.value)
        .whileTrue(new RunCommand(() -&gt; m_robotDrive.setX(), m_robotDrive));

    // set up arm preset positions
    new JoystickButton(m_driverController, XboxController.Button.kLeftBumper.value)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kScoringPosition)));

    new Trigger(
            () -&gt;
                m_driverController.getLeftTriggerAxis()
                    &gt; Constants.OIConstants.kTriggerButtonThreshold)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kIntakePosition)));
    // 7
    new JoystickButton(m_driverController, XboxController.Button.kStart.value)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kHomePosition)));

    // intake controls (run while button is held down, run retract command once when the button is
    // released)
    new Trigger(
            () -&gt;
                m_driverController.getRightTriggerAxis()
                    &gt; Constants.OIConstants.kTriggerButtonThreshold)
        .whileTrue(new RunCommand(() -&gt; m_intake.setPower(Constants.Intake.kIntakePower), m_intake))
        .onFalse(m_intake.retract());
    // 2
    new JoystickButton(m_driverController, XboxController.Button.kY.value)
        .whileTrue(new RunCommand(() -&gt; m_intake.setPower(1.0))); // was -1 - retracts intake

    // launcher controls (button to pre-spin the launcher and button to launch)
    new JoystickButton(m_driverController, XboxController.Button.kRightBumper.value)
        .whileTrue(new RunCommand(() -&gt; m_launcher.runLauncher(true), m_launcher));
    // 1
    // launcher to intake
    new JoystickButton(m_driverController, XboxController.Button.kB.value)
        .whileTrue(new RunCommand(() -&gt; m_launcher.runLauncher(false), m_launcher));
    // 12
    new JoystickButton(m_driverController, XboxController.Button.kA.value)
        .onTrue(m_intake.feedLauncher(m_launcher));
    // 11
    // right bumper revs forward for shooter
    // y revs inward for intake
    // a revs forward for intake

    //Joystick Config

    // 
    new JoystickButton(exampleJoystick, Position_Scoring_Button)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kScoringPosition)));

    new JoystickButton(exampleJoystick, Position_Intake_Button)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kIntakePosition)));

    new JoystickButton(exampleJoystick, Position_Home_Button)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kHomePosition)));

    new JoystickButton(exampleJoystick, Position_Moving_Button)
        .onTrue(new InstantCommand(() -&gt; m_arm.setTargetPosition(Constants.Arm.kMovingPostion)));
    // intake controls (run while button is held down, run retract command once when the button is
    // released)
    new JoystickButton(exampleJoystick, Action_Intake_Button)
        .whileTrue(new RunCommand(() -&gt; m_intake.setPower(Constants.Intake.kIntakePower), m_intake))
        .onFalse(m_intake.retract());

    new JoystickButton(m_driverController, XboxController.Button.kY.value)
        .whileTrue(new RunCommand(() -&gt; m_intake.setPower(1.0))); // was -1 - retracts intake

    // launcher controls (button to pre-spin the launcher and button to launch)
    new JoystickButton(m_driverController, Action_Prefire_Button)
        .whileTrue(new RunCommand(() -&gt; m_launcher.runLauncher(true), m_launcher));

    // launcher to intake
    new JoystickButton(m_driverController, Action_RunLauncherIntake_Button)
        .whileTrue(new RunCommand(() -&gt; m_launcher.runLauncher(false), m_launcher));

    new JoystickButton(m_driverController, Action_FeedLauncher_Button)
        .onTrue(m_intake.feedLauncher(m_launcher));

  }

  /**
   * Use this to pass the autonomous command to the main {@link Robot} class.
   *
   * @return the command to run in autonomous
   */
  public Command getAutonomousCommand() {
    // Create config for trajectory
    double speedMultiplier = 1;
    TrajectoryConfig config =
        new TrajectoryConfig(
                AutoConstants.kMaxSpeedMetersPerSecond * speedMultiplier,
                AutoConstants.kMaxAccelerationMetersPerSecondSquared)
            // Add kinematics to ensure max speed is actually obeyed
            .setKinematics(DriveConstants.kDriveKinematics);

    // An example trajectory to follow. All units in meters.

    Trajectory exampleTrajectory =
        TrajectoryGenerator.generateTrajectory(
            // Start at the origin facing the +X direction

            new Pose2d(0, 0, new Rotation2d(0)),
            // Drive forward m
            List.of(new Translation2d(1, 0), new Translation2d(1, 0), new Translation2d(2, 2)),
            // End  meters straight ahead of where we started, facing forward
            new Pose2d(3.6, 0, new Rotation2d(0)),
            config);

    new TrajectoryConfig(
            AutoConstants.kMaxSpeedMetersPerSecond,
            AutoConstants.kMaxAccelerationMetersPerSecondSquared)
        // Add kinematics to ensure max speed is actually obeyed
        .setKinematics(DriveConstants.kDriveKinematics);

    // new Pose2d(0, 0, new Rotation2d(4.1)),
    var thetaController =
        new ProfiledPIDController(
            AutoConstants.kPThetaController, 0, 0, AutoConstants.kThetaControllerConstraints);
    thetaController.enableContinuousInput(-Math.PI, Math.PI);

    SwerveControllerCommand swerveControllerCommand =
        new SwerveControllerCommand(
            exampleTrajectory,
            m_robotDrive::getPose, // Functional interface to feed supplier
            DriveConstants.kDriveKinematics,

            // Position controllers
            new PIDController(AutoConstants.kPXController, 0, 0),
            new PIDController(AutoConstants.kPYController, 0, 0),
            thetaController,
            m_robotDrive::setModuleStates,
            m_robotDrive);

    // Reset odometry to the starting pose of the trajectory.

    m_robotDrive.resetOdometry(exampleTrajectory.getInitialPose());

    // Run path following command, then stop at the end.
    return swerveControllerCommand.andThen(() -&gt; m_robotDrive.drive(0, 0, 0, false, false));
  }
}

