<p>package frc.robot.subsystems;</p>
<p>import com.fasterxml.jackson.annotation.JsonTypeInfo.Id;<br>
import com.revrobotics.CANSparkMax;<br>
import com.revrobotics.CANSparkMax.IdleMode;<br>
import com.revrobotics.CANSparkMaxLowLevel.MotorType;<br>
import edu.wpi.first.networktables.NetworkTableEntry;<br>
import edu.wpi.first.wpilibj.DigitalInput;<br>
import edu.wpi.first.wpilibj2.command.SubsystemBase;<br>
import frc.robot.Constants;<br>
import frc.robot.ShuffleboardInfo;</p>
<p>public class Tower extends SubsystemBase {</p>
<p>private final NetworkTableEntry towerUpperMotorEntry, towerLowerMotorEntry, towerLowerSensorEntry, towerUpperSensorEntry;</p>
<p>private final CANSparkMax lowerMotor = new CANSparkMax(Constants.LOWER_TOWER_MOTOR,MotorType.kBrushless);</p>
<p>private final CANSparkMax upperMotor = new CANSparkMax(Constants.UPPER_TOWER_MOTOR,MotorType.kBrushless);</p>
<p>private DigitalInput lowerSensor = new DigitalInput(Constants.LOWER_SENSOR_PORT_INPUT);</p>
<p>private DigitalInput upperSensor = new DigitalInput(Constants.UPPER_SENSOR_PORT_INPUT);</p>
<p>public Tower() {</p>
<pre><code>lowerMotor.restoreFactoryDefaults();
upperMotor.restoreFactoryDefaults();
lowerMotor.setInverted(true);
upperMotor.setInverted(true);
lowerMotor.setIdleMode(IdleMode.kBrake);
upperMotor.setIdleMode(IdleMode.kBrake);

towerUpperMotorEntry = ShuffleboardInfo.getInstance().getTowerUpperMotorEntry();
towerLowerMotorEntry = ShuffleboardInfo.getInstance().getTowerLowerMotorEntry();
towerUpperSensorEntry = ShuffleboardInfo.getInstance().getTowerUpperMotorEntry();
towerLowerSensorEntry = ShuffleboardInfo.getInstance().getTowerLowerMotorEntry();
</code></pre>
<p>}</p>
<p>/** Sets the upper motor speed for the tower. */<br>
public void setSpeedUpper(double speed) {<br>
upperMotor.set(speed);<br>
}</p>
<p>/** Returns the set speed of the upper motor. */<br>
public double getSpeedUpper() {<br>
return upperMotor.get();<br>
}</p>
<p>/** Sets the speed of the lower tower motor. */<br>
public void setSpeedLower(double speed) {<br>
lowerMotor.set(speed);<br>
}</p>
<p>/** Returns the set speed of the lower motor */<br>
public double getSpeedLower() {<br>
return lowerMotor.get();<br>
}</p>
<p>public boolean isBallHereUpper() {<br>
return upperSensor.get();<br>
}</p>
<p>public boolean isBallHereLower() {<br>
return lowerSensor.get();<br>
}</p>
<p>public void stop() {<br>
lowerMotor.set(0);<br>
upperMotor.set(0);<br>
}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void periodic() {<br>
towerUpperMotorEntry.setDouble(getSpeedUpper());<br>
towerLowerMotorEntry.setDouble(getSpeedLower());</p>
<p>// This does not correctly show the status of the beam break<br>
towerUpperSensorEntry.setBoolean(isBallHereUpper());<br>
towerLowerSensorEntry.setBoolean(isBallHereLower());<br>
}</p>
<p>}</p>
<p>package frc.robot.commands;</p>
<p>import edu.wpi.first.wpilibj2.command.CommandBase;<br>
import frc.robot.Constants;<br>
import frc.robot.subsystems.Tower;<br>
public class LoadTower extends CommandBase {</p>
<p>private Tower tower;</p>
<p>//checks if command should end<br>
private boolean finished;<br>
/** Creates a new ToggleTower. <em>/<br>
/</em><br>
Both tower motors will spin until sensor is set to false(the laser is tripped)<br>
subsequent presses will turn the motors on and off, respectively<br>
*/</p>
<p>public LoadTower(Tower tower) {<br>
addRequirements(tower);<br>
this.tower = tower;<br>
}</p>
<p>// Called when the command is initially scheduled.<br>
<a class="mention" href="/u/override">@Override</a><br>
public void initialize() {</p>
<pre><code>tower.setSpeedLower(Constants.TOWER_SPEED);
tower.setSpeedUpper(Constants.TOWER_SPEED);
finished = false;
</code></pre>
<p>}</p>
<p>// Called every time the scheduler runs while the command is scheduled.<br>
<a class="mention" href="/u/override">@Override</a><br>
public void execute() {</p>
<pre><code>//sensor to check if balls are in stored position
if(tower.isBallHereUpper()){
  tower.setSpeedUpper(0);
}
  if(tower.isBallHereLower() &amp;&amp; tower.isBallHereUpper()){
    tower.setSpeedLower(0);
    finished = true;
  }
</code></pre>
<p>}</p>
<p>// Called once the command ends or is interrupted.<br>
<a class="mention" href="/u/override">@Override</a></p>
<p>public void end(boolean interrupted) {<br>
tower.setSpeedUpper(0);<br>
tower.setSpeedLower(0);<br>
}</p>
<p>// Returns true when the command should end.<br>
<a class="mention" href="/u/override">@Override</a><br>
public boolean isFinished() {<br>
//ends the command if the corresponding main is finished<br>
return finished;<br>
}<br>
}</p>","post_number":3,"post_type":1,"posts_count":7,"updated_at":"2022-03-13T21:58:35.593Z","reply_count":0,"reply_to_post_number":2,"quote_count":0,"incoming_link_count":2,"reads":34,"readers_count":33,"score":16.8,"yours":false,"topic_id":405548,"topic_slug":"accessor-method-for-digitalinput-always-returns-false","display_username":null,"primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":2,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"reply_to_user":{"id":29225,"username":"Daltz3","name":"Dalton Smith","avatar_template":"/user_avatar/www.chiefdelphi.com/daltz3/{size}/145991_2.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":51325,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/accessor-method-for-digitalinput-always-returns-false/405548/3","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3022778,"name":"Dan Langdon","username":"woodworker2001","avatar_template":"/letter_avatar_proxy/v4/letter/w/bbe5ce/{size}.png","created_at":"2022-03-14T16:44:00.708Z","cooked":"<p>You are not comparing the value returned to anything.</p>
