}</p>","post_number":1,"post_type":1,"posts_count":7,"updated_at":"2023-06-15T03:28:32.518Z","reply_count":1,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":13,"reads":161,"readers_count":160,"score":102.2,"yours":false,"topic_id":438191,"topic_slug":"using-same-button-for-multiple-solenoids-ifs-and-variables","display_username":"Zane McElroy","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":46275,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/using-same-button-for-multiple-solenoids-ifs-and-variables/438191/1","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null,"can_vote":false},{"id":3214354,"name":"Owen Busler","username":"Owen_Busler","avatar_template":"/user_avatar/www.chiefdelphi.com/owen_busler/{size}/7684_2.png","created_at":"2023-06-15T03:41:14.818Z","cooked":"<aside class="quote no-group quote-modified" data-username="CoachZM" data-post="1" data-topic="438191">
