<pre><code class="lang-java">package frc.robot;

import edu.wpi.first.wpilibj.Joystick;
import edu.wpi.first.wpilibj.TimedRobot;
import edu.wpi.first.wpilibj.drive.DifferentialDrive;
import edu.wpi.first.wpilibj.motorcontrol.MotorControllerGroup;
import edu.wpi.first.wpilibj.motorcontrol.PWMSparkMax;
import edu.wpi.first.wpilibj.motorcontrol.Spark;
import edu.wpi.first.wpilibj.motorcontrol.VictorSP;

/**

* This is a demo program showing the use of the DifferentialDrive class. Runs the motors with
* arcade steering.
*/
public class Robot extends TimedRobot {

private final PWMSparkMax m_frontLeft = new PWMSparkMax(9);
private final PWMSparkMax m_rearLeft = new PWMSparkMax(8);
private final MotorControllerGroup m_left = new MotorControllerGroup(m_frontLeft, m_rearLeft);

private final PWMSparkMax m_frontRight = new PWMSparkMax(7);
private final PWMSparkMax m_rearRight = new PWMSparkMax(6);
private final MotorControllerGroup m_right = new MotorControllerGroup(m_frontRight, m_rearRight);

private final DifferentialDrive m_robotDrive = new DifferentialDrive(m_left, m_right);
private final Joystick m_0stick = new Joystick(0);

private final PWMSparkMax m_chainMotor = new PWMSparkMax(3 );
private final PWMSparkMax m_clawMotor = new PWMSparkMax(2);
private final DifferentialDrive m_robotarm = new DifferentialDrive(m_chainMotor, m_clawMotor);
private final Joystick m_1stick = new Joystick(1);

VictorSP victor2 = new VictorSP( 2); // 0 is the RIO PWM port this is connected to
VictorSP victor1 = new VictorSP( 3); // 0 is the RIO PWM port this is connected to
victor2**.**set(0.1**)&lt;strong&gt;; // the % output of the motor, between -1 and 1
victor1.set(0.1&lt;/strong&gt;)**; // the % output of the motor, between -1 and 1

@Override
public void robotInit() {
// We need to invert one side of the drivetrain so that positive voltages
// result in both sides moving forward. Depending on how your robot’s
// gearbox is constructed, you might have to invert the left side instead.
m_right.setInverted(true);
}

@Override
public void teleopPeriodic() {
// Drive with arcade drive.
// That means that the Y axis drives forward
// and backward, and the X turns left and right.
m_robotDrive.arcadeDrive(-m_0stick.getY(), -m_0stick.getX());
m_robotarm.arcadeDrive(-m_1stick.getY(), -m_1stick.getX());

}

}
</code></pre>
