<pre><code>gcomp.disable();
</code></pre>
<p>}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void teleopPeriodic() {</p>
<pre><code>double speed = -driverJoystick.getRawAxis(1) * 0.6;
double turn = driverJoystick.getRawAxis(4) * 0.6;

gdrive.arcadeDrive(speed * 0.6, turn * 0.3);

gdrive.setSafetyEnabled(true);

boolean emergenceDriver = driverJoystick.getRawButtonPressed(2);

if (emergenceDriver == true) {
  leftMotor1.stopMotor();
  leftMotor2.stopMotor();
  rightMotor1.stopMotor();
  rightMotor2.stopMotor();
  armRotate.stopMotor();
}

double rotatePower = operatorJoystick.getRawAxis(1) * 0.8 ;

armRotate.set(rotatePower);

if (operatorJoystick.getRawButton(1)) {

  solenoid.set(DoubleSolenoid.Value.kForward);

} else if (operatorJoystick.getRawButton(2)) {

  solenoid.set(DoubleSolenoid.Value.kReverse);

}

