to_post_number":null,"quote_count":0,"incoming_link_count":0,"reads":70,"readers_count":69,"score":24.0,"yours":false,"topic_id":427884,"topic_slug":"wpilibj-counter-object","display_username":"Justin Spurlock","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":57508,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"Snake51"},"post_url":"/t/wpilibj-counter-object/427884/8","custom_user_title":"Snake51","reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":true},{"id":3151574,"name":"Peter Johnson","username":"Peter_Johnson","avatar_template":"/user_avatar/www.chiefdelphi.com/peter_johnson/{size}/10417_2.png","created_at":"2023-02-23T23:37:11.380Z","cooked":"<p>EDIT: I think you can just set an arbitrary unused DIO as the direction (“down”) source.  It’s always going to read the same direction, so setReverseDirection() will “reverse” it.</p>
<p><s>Oh. Yeah, it doesn’t quite work, as the setReverseDirection() just reverses the down source edge.</s>  Another way to do what you’re trying to do is to create an up/down counter where both the up and down sources are the same DIO, and then change the Up and Down source edges to enable/disable either the up or down direction?</p>
<pre><code class="lang-java">Counter counter = new Counter(Counter.Mode.k2Pulse);
DigitalInput in = new DigitalInput(1);
counter.setUpSource(in);
counter.setDownSource(in);
counter.setUpSourceEdge(true, false);
counter.setDownSourceEdge(false, false);

...
if (speed &gt;= 0) {
  counter.setDownSourceEdge(false, false);
  counter.setUpSourceEdge(true, false);
} else {
  counter.setUpSourceEdge(false, false);
  counter.setDownSourceEdge(true, false);
}
</code></pre>","post_number":9,"post_type":1,"posts_count":13,"updated_at":"2023-02-23T23:51:41.094Z","reply_count":1,"reply_to_post_number":8,"quote_count":0,"incoming_link_count":0,"reads":65,"readers_count":64,"score":18.0,"yours":false,"topic_id":427884,"topic_slug":"wpilibj-counter-object","display_username":"Peter Johnson","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":2,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":57508,"username":"Snake51","name":"Justin Spurlock","avatar_template":"/letter_avatar_proxy/v4/letter/s/90ced4/{size}.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":13575,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"Volunteer WPILib Developer, CSA"},"post_url":"/t/wpilibj-counter-object/427884/9","custom_user_title":"Volunteer WPILib Developer, CSA","reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":true},{"id":3151659,"name":"Joe Ross","username":"Joe_Ross","avatar_template":"/user_avatar/www.chiefdelphi.com/joe_ross/{size}/37_2.png","created_at":"2023-02-24T02:16:44.175Z","cooked":"<p>I tried doing something similar years ago and there was enough lag from when the commanded speed flipped direction until the motor flipped direction that you couldn’t get accurate position. I’d recommend looking at other sensors rather then trying a kludge like this.</p>","post_number":10,"post_type":1,"posts_count":13,"updated_at":"2023-02-24T02:16:44.175Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming