st_number":null,"quote_count":0,"incoming_link_count":3,"reads":119,"readers_count":118,"score":43.8,"yours":false,"topic_id":402727,"topic_slug":"example-java-using-differentialdrive-tank-drive-for-falcon-500-controller","display_username":"Rick","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":38140,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/example-java-using-differentialdrive-tank-drive-for-falcon-500-controller/402727/3","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3015758,"name":"Andy Agan","username":"Andy_7425","avatar_template":"/letter_avatar_proxy/v4/letter/a/3be4f8/{size}.png","created_at":"2022-03-02T19:24:38.670Z","cooked":"<p>I am having an issue as well. Would you mind sharing your code with me?</p>","post_number":4,"post_type":1,"posts_count":6,"updated_at":"2022-03-02T19:25:32.110Z","reply_count":1,"reply_to_post_number":3,"quote_count":0,"incoming_link_count":28,"reads":97,"readers_count":96,"score":164.4,"yours":false,"topic_id":402727,"topic_slug":"example-java-using-differentialdrive-tank-drive-for-falcon-500-controller","display_username":"Andy Agan","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":3,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"reply_to_user":{"id":38140,"username":"Mentor5","name":"Rick","avatar_template":"/letter_avatar_proxy/v4/letter/m/3d9bf3/{size}.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":37387,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"Mentor/Coach"},"post_url":"/t/example-java-using-differentialdrive-tank-drive-for-falcon-500-controller/402727/4","custom_user_title":"Mentor/Coach","reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3015782,"name":"Rick","username":"Mentor5","avatar_template":"/letter_avatar_proxy/v4/letter/m/3d9bf3/{size}.png","created_at":"2022-03-02T20:03:52.821Z","cooked":"<p>The basic issue we were having was not having the latest Phoenix library. We wanted to use a DifferentialDrive in our Drive train subsystem class, but the constructor wanted the a MotorController type and we couldn’t find a class for a Falcon 500 that extended from MotorController.<br>
The fix was to manage vendor libraries, which you can get to by right clicking on the build.gradle file in the visual studio code tree. (instructions here: <a href="https://docs.ctre-phoenix.com/en/stable/ch05a_CppJava.html#frc-c-java-add-phoenix" rel="noopener nofollow ugc">FRC: VS Code C++/Java — Phoenix documentation (ctre-phoenix.com)</a></p>
<p>Our robot uses subsystems and the code for our drive train is below:<br>
<code><br>
package frc.robot.subsystems;</code></p><code>
<p>import com.ctre.phoenix.motorcontrol.Faults;</p>
<p>import com.ctre.phoenix.motorcontrol.FeedbackDevice;</p>
<p>import com.ctre.phoenix.motorcontrol.InvertType;</p>
<p>import com.ctre.phoenix.motorcontrol.TalonFXInvertType;</p>
<p>import com.ctre.phoenix.motorcontrol.can.TalonFX;</p>
<p>import com.ctre.phoenix.motorcontrol.can.TalonFXConfiguration;</p>
<p>import com.ctre.phoenix.motorcontrol.can.WPI_TalonFX;</p>
<p>import edu.wpi.first.wpilibj2.command.Subsystem;</p>
<p>import edu.wpi.first.wpilibj2.command.SubsystemBase;</p>
<p>import edu.wpi.first.wpilibj.drive.DifferentialDrive;</p>
<p>import edu.wpi.first.wpilibj.motorcontrol.MotorControllerGroup;</p>
<p>import frc.robot.Constants.DriveConstants;</p>
<p>import frc.robot.Constants.XboxControllerButtonMappings;</p>
<p>import edu.wpi.first.wpilibj.smartdashboard.SendableChooser;</p>
<p>import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;</p>
<p>public class DriveTrainSubsystem extends SubsystemBase {</p>
<pre><code>// A whole lot of motors.

// First letter is which side of the drive train

// motor is on, looking from the side of the intake.

// Second letter is which of the three, T is top,

// F is front, where the shooter is ; B is back where the intake is.



// Master motors

WPI_TalonFX LT = new WPI_TalonFX(DriveConstants.LTFalconID);

WPI_TalonFX RT = new WPI_TalonFX(DriveConstants.RTFalconID);



// Follower motors

WPI_TalonFX LF = new WPI_TalonFX(DriveConstants.LFFalconID);

WPI_TalonFX LB = new WPI_TalonFX(DriveConstants.LBFalconID);

WPI_TalonFX RF = new WPI_TalonFX(DriveConstants.RFFalconID);

WPI_TalonFX RB = new WPI_TalonFX(DriveConstants.RBFalconID);

DifferentialDrive _drive = new DifferentialDrive(LT, RT);

private double speedMod = 0.5;

public DriveTrainSubsystem() {

    LT.configFactoryDefault();

    LF.configFactoryDefault();

    LB.configFactoryDefault();

    RT.configFactoryDefault();

    RF.configFactoryDefault();

    RB.configFactoryDefault();

    LF.follow(LT);

    LB.follow(LT);

    RF.follow(RT);

    RB.follow(RT);

    LT.setInverted(true);

    RT.setInverted(false);

    LF.setInverted(false);

    LB.setInverted(false);

    RF.setInverted(true);

    RB.setInverted(true);

    //Config neutral deadband to be the smallest possible

    LT.configNeutralDeadband(0.001);

    RT.configNeutralDeadband(0.001);

    /* Config the peak and nominal outputs */

    LT.configNominalOutputForward(0, 30);

    LT.configNominalOutputReverse(0, 30);

    LT.configPeakOutputForward(1, 30);

    LT.configPeakOutputReverse(-1, 30);

    RT.configNominalOutputForward(0, 30);

    RT.configNominalOutputReverse(0, 30);

    RT.configPeakOutputForward(1, 30);

    RT.configPeakOutputReverse(-1, 30);

            /* Config the Velocity closed loop gains in slot0 */

    LT.config_kF(0, DriveConstants.kFFvelocity, 30);

    LT.config_kP(0, DriveConstants.kPvelocity, 30);

    LT.config_kI(0, 0, 30);

    LT.config_kD(0, 0, 30);



}

public void arcadeDrive(double speed, double rotation) {

    _drive.arcadeDrive(-speed * speedMod, rotation * 0.5);

}

// Reusable method for setting different robot speeds.

public void changeSpeed(double s) {

    speedMod = s;

}
</code></pre>
