import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj2.command.SubsystemBase;
import frc.robot.Constants.ElevatorConstants;

public class Elevator extends SubsystemBase {
    // Create a PID controller whose setpoint's change is subject to maximum
  // velocity and acceleration constraints.

  private final TrapezoidProfile.Constraints m_constraints =
      new TrapezoidProfile.Constraints(ElevatorConstants.kMaxVelocity, ElevatorConstants.kMaxAcceleration);
  private final ProfiledPIDController m_controller =
      new ProfiledPIDController(
        ElevatorConstants.kP, 
        ElevatorConstants.kI, 
        ElevatorConstants.kD, 
        m_constraints, 
        ElevatorConstants.kDt);

  private final Encoder m_encoder = new Encoder(2, 3);
  private final SparkMax m_motor = new SparkMax(6, MotorType.kBrushless);
  private final SparkMax m_motor2 = new SparkMax(7, MotorType.kBrushless);

  private final ElevatorFeedforward m_feedforward = new ElevatorFeedforward(
    ElevatorConstants.kS, 
    ElevatorConstants.kG, 
    ElevatorConstants.kV);



    public Elevator()
    {
        //Elevator PID
       // m_encoder.setDistancePerPulse(1.0 / 360.0 * 2.0 * Math.PI * 0.5);
       m_encoder.setDistancePerPulse(1.0 / 2048.0 * Math.PI * 1.625);
       m_encoder.reset();

    }

    public void initializeElevator()
    {
      m_encoder.reset();


    }
    public void ElevatorChange(boolean aButtonPressed, boolean bButtonPressed, boolean xButtonPressed, boolean yButtonPressed, double armPosition)
    {
          SmartDashboard.putNumber("Encoder get distance:", m_encoder.getDistance());

              if (aButtonPressed) {
                // L1
                m_controller.setGoal(0.05);

              } 
              else if (xButtonPressed) {
                // L2
                m_controller.setGoal(3);

                }
                else if (yButtonPressed)
                {
                  // L3
                  m_controller.setGoal(5);
                }
                else if (bButtonPressed)
                {
                  // L4
                  m_controller.setGoal(7);
                } 

              m_motor.setVoltage( 
                -(m_controller.calculate(m_encoder.getDistance())
                    + m_feedforward.calculate(m_controller.getSetpoint().velocity)));

              m_motor2.setVoltage( 
                  (m_controller.calculate(m_encoder.getDistance())
                      + m_feedforward.calculate(m_controller.getSetpoint().velocity)));


    }
  }

//Constants used for the robot
