public class LED extends SubsystemBase {

  public Color kFearTeal = new Color(0, 122, 133);

  private AddressableLED m_led = new AddressableLED(0);
  private AddressableLEDBuffer m_ledBuffer = new AddressableLEDBuffer(8 +
      LEDConstants.kLEDLength);

  private AddressableLEDBufferView m_CANdleView = m_ledBuffer.createView(0, 7);
  // private static AddressableLEDBufferView m_CANdleView1 = m_ledBuffer.createView(1, 1);
  // private static AddressableLEDBufferView m_CANdleView2 = m_ledBuffer.createView(2, 2);
  // private static AddressableLEDBufferView m_CANdleView3 = m_ledBuffer.createView(3, 3);
  // private static AddressableLEDBufferView m_CANdleView4 = m_ledBuffer.createView(4, 4);
  // private static AddressableLEDBufferView m_CANdleView5 = m_ledBuffer.createView(5, 5);
  // private static AddressableLEDBufferView m_CANdleView6 = m_ledBuffer.createView(6, 6);
  // private static AddressableLEDBufferView m_CANdleView7 = m_ledBuffer.createView(7, 7);

  private AddressableLEDBufferView m_left = m_ledBuffer.createView(8, 42);
  private AddressableLEDBufferView m_right = m_ledBuffer.createView(43,
      77).reversed();

  private EndEffector m_endEffector;

  private LEDPattern m_stow = LEDPattern.solid(kFearTeal);
  private LEDPattern m_stowWithGamePiece = LEDPattern.solid(Color.kLime);
  private LEDPattern m_intaking = LEDPattern.solid(Color.kRed);
  private LEDPattern m_scoring = LEDPattern.solid(Color.kLime).blink(Seconds.of(0.02));

  private LEDPattern m_elevatorBaseStow = LEDPattern.solid(kFearTeal);
  private LEDPattern m_elevatorBaseHoldingGamePiece = LEDPattern.solid(Color.kLime);
  private LEDPattern m_elevatorMask;
  private LEDPattern m_elevatorHeightDisplay;

  private LEDPattern m_rainbow = LEDPattern.rainbow(255, 128).scrollAtAbsoluteSpeed(MetersPerSecond.of(1),
      LEDConstants.kLedSpacing);

  /** Creates a new LED. */
  public LED(EndEffector endeffector, Elevator elevator) {
    m_endEffector = endeffector;
    m_elevatorMask = LEDPattern.progressMaskLayer(() -&gt; elevator.getPosition() / elevator.getDesiredPosition());

    m_stowWithGamePiece.applyTo(m_CANdleView);

    m_led.setLength(m_ledBuffer.getLength());
    m_led.setData(m_ledBuffer);
    m_led.start();

    setDefaultCommand(setLEDsToEndeffectorState(m_endEffector::getCurrentState).ignoringDisable(true));
  }

  @Override
  public void periodic() {
    m_led.setData(m_ledBuffer);
  }

  public Command setLEDsToEndeffectorState(Supplier&lt;EndEffectorState&gt; endeffectorStateSupplier) {
    return run(() -&gt; {
      if (endeffectorStateSupplier.get() == EndEffectorState.HOLD_ALGAE
          || endeffectorStateSupplier.get() == EndEffectorState.HOLD_CORAL) {
        m_stowWithGamePiece.applyTo(m_left);
        m_stowWithGamePiece.applyTo(m_right);
      } else if (endeffectorStateSupplier.get() == EndEffectorState.INTAKING_ALGAE
          || endeffectorStateSupplier.get() == EndEffectorState.INTAKING_CORAL) {
        m_intaking.applyTo(m_left);
        m_intaking.applyTo(m_right);
      } else if (endeffectorStateSupplier.get() == EndEffectorState.SCORING_CORAL
          || endeffectorStateSupplier.get() == EndEffectorState.OUTTAKING_ALGAE) {
        m_scoring.applyTo(m_left);
        m_scoring.applyTo(m_right);
      } else {
        m_stow.applyTo(m_left);
        m_stow.applyTo(m_right);
      }
    }).unless(DriverStation::isAutonomous);
  }

  public Command runElevatorPattern() {
    Supplier&lt;EndEffectorState&gt; endeffectorStateSupplier = m_endEffector::getCurrentState;
    return new FunctionalCommand(() -&gt; {
    }, () -&gt; {
      if (endeffectorStateSupplier.get() == EndEffectorState.HOLD_ALGAE
          || endeffectorStateSupplier.get() == EndEffectorState.HOLD_CORAL) {
        m_elevatorHeightDisplay = m_elevatorBaseHoldingGamePiece.mask(m_elevatorMask);
      } else {
        m_elevatorHeightDisplay = m_elevatorBaseStow.mask(m_elevatorMask);
      }
      m_elevatorHeightDisplay.applyTo(m_left);
      m_elevatorHeightDisplay.applyTo(m_right);
    }, (interrupted) -&gt; {
    }, () -&gt; false, this).unless(DriverStation::isAutonomous);
  }

  public Command runAutoLEDs() {
    return new FunctionalCommand(() -&gt; {
    }, () -&gt; {
      m_rainbow.applyTo(m_left);
      m_rainbow.applyTo(m_right);
    }, (interrupted) -&gt; {
    }, () -&gt; !DriverStation.isAutonomous(), this);
  }

  // public static Command setCANivoreLed(LEDPattern pattern, int CANdleLED) {
