<pre><code>public class SmartIntake extends CommandBase {
    private final Intake m_rightIntake;
    private final Intake m_leftIntake;
    private final Drivetrain m_drive;
    private final Timer m_timer = new Timer();
    private boolean m_rightRunning = false;
    private boolean m_leftRunning = false;
    private double m_leftStopTime = 0.0;
    private double m_rightStopTime = 0.0;
    private double yVelAve = 0.0;

    public SmartIntake(Intake right, Intake left, Drivetrain drive) {
        m_leftIntake = left;
        m_rightIntake = right;
        m_drive = drive;
        addRequirements(left, right);
    }

    @Override
    public void initialize() {
        m_timer.reset();
        m_timer.start();
        double yVel = m_drive.getChassisSpeed().vyMetersPerSecond;

        if (yVel &gt; 0.05) {
            m_leftIntake.extend();
            m_leftIntake.run(IntakeConstants.kSpeed);
            m_leftRunning = true;
        } else if (yVel &lt; -0.05) {
            m_rightIntake.extend();
            m_rightIntake.run(IntakeConstants.kSpeed);
            m_rightRunning = true;
        }

    }

    @Override
    public void execute() {
        final double currentTime = m_timer.get();
        double yVel = m_drive.getChassisSpeed().vyMetersPerSecond;
        yVelAve = (3 * yVelAve + yVel) / 4.0;

        if (yVelAve &gt; 0.5) {
            yVelAve = 0.5;
        } else if (yVelAve &lt; -0.5) {
            yVelAve = -0.5;
        }

        if (yVelAve &lt; -0.20) {
            if (m_leftRunning) {
                m_leftIntake.retract();
                m_leftRunning = false;
                m_leftStopTime = currentTime;
            }

            m_rightIntake.extend();
            m_rightIntake.run(IntakeConstants.kSpeed);
        } else if (yVelAve &gt; 0.20) {
            if (m_rightRunning) {
                m_rightIntake.retract();
                m_rightRunning = false;
                m_rightStopTime = currentTime;
            }
            m_leftRunning = true;
            m_leftIntake.extend();
            m_leftIntake.run(IntakeConstants.kSpeed);
        }

        if (!m_leftRunning &amp;&amp; currentTime &gt; m_leftStopTime + 0.80) {
            m_leftIntake.stop();
        }

        if (!m_rightRunning &amp;&amp; currentTime &gt; m_rightStopTime + 0.80) {
            m_rightIntake.stop();
        }

        SmartDashboard.putBoolean(m_leftIntake.getID() + " Running", m_leftRunning);
        SmartDashboard.putBoolean(m_rightIntake.getID() + " Running", m_rightRunning);

    }

    @Override
    public void end(boolean interrupted) {
        m_rightRunning = false;
        m_leftRunning = false;
        SmartDashboard.putBoolean(m_leftIntake.getID() + " Running", false);
        SmartDashboard.putBoolean(m_rightIntake.getID() + " Running", false);
        m_leftStopTime = 0.0;
        m_rightStopTime = 0.0;
        m_leftIntake.retract();
        m_rightIntake.retract();
        yVelAve = 0.0;
        m_timer.stop();
    }
}</code></pre>","post_number":17,"post_type":1,"posts_count":33,"updated_at":"2022-07-02T06:56:14.030Z","reply_count":3,"reply_to_post_number":16,"quote_count":0,"incoming_link_count":9,"reads":468,"readers_count":467,"score":183.6,"yours":false,"topic_id":410494,"topic_slug":"shoot-while-move-code-1706","display_username":"Emerson OHara","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":50815,"username":"Rezq","name":"Reza","avatar_template":"/user_avatar/www.chiefdelphi.com/rezq/{size}/178141_2.png"},"bookmarked":false,"actions_summary":[{"id":2,"count":2}],"moderator":false,"admin":false,"staff":false,"user_id":43759,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"1706 Drive Coach | 9401 Mentor | #TeamThrifty"},"post_url":"/t/shoot-while-move-code-1706/410494/17","custom_user_title":"1706 Drive Coach | 9401 Mentor | #TeamThrifty","reactions":[{"id":"heart","type":"emoji","count":2}],"current_user_reaction":null,"reaction_users_count":2,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3071310,"name":"JJ Sessa","username":"jjsessa","avatar_template":"/user_avatar/www.chiefdelphi.com/jjsessa/{size}/8845_2.png","created_at":"2022-07-02T07:58:46.686Z","cooked":"<aside class="quote no-group" data-username="Emerson1706" data-post="17" data-topic="410494">
