<pre><code> public class Robot extends TimedRobot {
 Spark leftRear = new Spark(0);
 Spark leftFront = new Spark(1);
 Spark rightRear = new Spark(5);
 Spark rightFront = new Spark(6);
 MotorControllerGroup Left = new MotorControllerGroup(leftRear, leftFront);
 MotorControllerGroup Right = new MotorControllerGroup(rightRear, rightFront);
 DifferentialDrive drive = new DifferentialDrive(Left, Right);
 XboxController xboxController = new XboxController(0);

 PhotonCamera camera = new PhotonCamera("Camera");
 final double CAMERA_HEIGHT_METERS = Units.inchesToMeters(24);
 final double TARGET_HEIGHT_METERS = Units.feetToMeters(5);
 final double CAMERA_PITCH_RADIANS = Units.degreesToRadians(0);
 final double GOAL_RANGE_METERS = Units.feetToMeters(3);

 final double LINEAR_P = 0.1;
 final double LINEAR_D = 0.0;
 PIDController forwardController = new PIDController(LINEAR_P, 0, LINEAR_D);

 final double ANGULAR_P = 0.1;
 final double ANGULAR_D = 0.0;
 PIDController turnController = new PIDController(ANGULAR_P, 0, ANGULAR_D);

 @Override
 public void teleopPeriodic() {

double forwardSpeed;
    double rotationSpeed;

    forwardSpeed = xboxController.getRightX();

    if (xboxController.getRawButton(1)) {
        // Vision-alignment mode
        // Query the latest result from PhotonVision
        var result = camera.getLatestResult();

        if (result.hasTargets()) {
            // Calculate angular turn power
            // -1.0 required to ensure positive PID controller effort _increases_ yaw
            rotationSpeed = -turnController.calculate(result.getBestTarget().getYaw(), 1);
        } else {
            // If we have no targets, stay still.
            rotationSpeed = 0;
        }
    } else {
        // Manual Driver Mode
        rotationSpeed = xboxController.getLeftY();
    }

    // Use our forward/turn speeds to control the drivetrain
    drive.arcadeDrive(forwardSpeed, rotationSpeed);

}
}</code></pre>","post_number":6,"post_type":1,"posts_count":24,"updated_at":"2022-04-03T19:21:49.975Z","reply_count":1,"reply_to_post_number":5,"quote_count":0,"incoming_link_count":1,"reads":85,"readers_count":84,"score":27.0,"yours":false,"topic_id":407685,"topic_slug":"photonvision-not-working","display_username":"Konnor/RetroBuzz | 5517AM","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":9353,"username":"Jreed129","name":"Jeremy Reed","avatar_template":"/user_avatar/www.chiefdelphi.com/jreed129/{size}/168565_2.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":49593,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"RetroBuzz | 5517AM YAGSL Contributor"},"post_url":"/t/photonvision-not-working/407685/6","custom_user_title":"RetroBuzz | 5517AM YAGSL Contributor","reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3036002,"name":"Chris Gerth","username":"gerthworm","avatar_template":"/user_avatar/www.chiefdelphi.com/gerthworm/{size}/268658_2.png","created_at":"2022-04-03T19:23:08.728Z","cooked":"<aside class="quote no-group" data-username="Konnor" data-post="4" data-topic="407685">
