        leftEncoder = new Encoder(0, 1, false, EncodingType.k4X);
        rightEncoder = new Encoder(2, 3, true, EncodingType.k4X);
        gyroscope = new ADXRS450_Gyro();

        // reset encoders and gyroscope
        leftEncoder.reset();
