<em>import org.littletonrobotics.junction.wpilog.WPILOGWriter.AdvantageScopeOpenBehavior;</em></p>
<p><em>import edu.wpi.first.wpilibj2.command.Command;</em><br>
<em>import edu.wpi.first.wpilibj2.command.CommandScheduler;</em><br>
<em>import frc.robot.Constants.RunMode;</em></p>
<p><em>public class Robot extends LoggedRobot {</em></p>
<pre><code>private Command m_autonomousCommand;

private final RobotContainer m_robotContainer;

public static RunMode runMode = RunMode.REAL;

public Robot() {
    Logger.recordMetadata("2025-Season-Code", "Robot-Code"); // Set a metadata value

    if (!isReal()) {
        runMode = RunMode.SIMULATION;
        runReplay();
        setUseTiming(false);
    }
    initLogging();


    Logger.start(); // Start logging! No more data receivers, replay sources, or metadata values may be added.

    m_robotContainer = new RobotContainer();
    System.out.println(runMode);


}

public void runReplay() {
    runMode = RunMode.REPLAY;
    String logPath = "C:/Users/Maya Lev/Desktop/2025-Offseason/2025-Offseason/logs/akit_25-07-25_15-42-50.wpilog"; // Pull the replay log from AdvantageScope (or prompt the user)
    Logger.setReplaySource(new WPILOGReader(logPath)); // Read replay log
    Logger.addDataReceiver(new WPILOGWriter(LogFileUtil.addPathSuffix(logPath, "_sim"), AdvantageScopeOpenBehavior.ALWAYS)); // Save outputs to a new log

}

public void initLogging() {
    Logger.addDataReceiver(new NT4Publisher());
    Logger.addDataReceiver(new WPILOGWriter()); 
}
</code></pre>
