import edu.wpi.first.math.geometry.Translation3d;
import edu.wpi.first.math.kinematics.SwerveDriveKinematics;
import edu.wpi.first.math.kinematics.SwerveModulePosition;
import edu.wpi.first.math.util.Units;
import edu.wpi.first.wpilibj2.command.SubsystemBase;
import frc.robot.CommandSwerveDrivetrain;
import frc.robot.RobotContainer;
import frc.robot.Constants.IDConstants;
import frc.robot.generated.TunerConstants;

public class VisionPoseEstimation extends SubsystemBase {
    private final SwerveModule swerveModule[];
    private final Translation2d modulePositions[];
    private final SwerveDriveKinematics kinematics;
    private CommandSwerveDrivetrain drivetrain = TunerConstants.DriveTrain;

    // Photon and pose estimation
    public static final PhotonCamera photonCamera = new PhotonCamera("photonCam");
    public static final Transform3d camPosition = new Transform3d(new Translation3d(0, 0, 0), new Rotation3d(0, 0, 0));
    private static final AprilTagFieldLayout field = AprilTagFields.k2024Crescendo.loadAprilTagLayoutField();
    public PhotonPoseEstimator photonPose = new PhotonPoseEstimator(field, PoseStrategy.MULTI_TAG_PNP_ON_COPROCESSOR,
    photonCamera, camPosition);
    public SwerveDrivePoseEstimator swervePoseEstimator;
    public Translation2d translation;
    public Rotation2d rotation;
    public Pigeon2 gyro = drivetrain.getPigeon2();

    public VisionPoseEstimation() {
        swerveModule = new SwerveModule[] {
                new SwerveModule(TunerConstants.FrontLeft, getName()),
                new SwerveModule(TunerConstants.FrontRight, getName()),
                new SwerveModule(TunerConstants.BackLeft, getName()),
                new SwerveModule(TunerConstants.BackRight, getName())
        };

        kinematics = new SwerveDriveKinematics(
                modulePositions = new Translation2d[] {
                        new Translation2d(Units.inchesToMeters(TunerConstants.kFrontLeftXPosInches), // front left
                                Units.inchesToMeters(TunerConstants.kFrontLeftYPosInches)),
                        new Translation2d(Units.inchesToMeters(TunerConstants.kFrontRightXPosInches), // front right
                                Units.inchesToMeters(TunerConstants.kFrontRightYPosInches)),
                        new Translation2d(Units.inchesToMeters(TunerConstants.kBackLeftXPosInches), // back left
                                Units.inchesToMeters(TunerConstants.kBackLeftYPosInches)),
                        new Translation2d(Units.inchesToMeters(TunerConstants.kBackRightXPosInches),
                                (Units.inchesToMeters(TunerConstants.kBackRightYPosInches)))
                });
        swervePoseEstimator = new SwerveDrivePoseEstimator(kinematics, gyro.getRotation2d(),
                new SwerveModulePosition[] {
                        swerveModule[0].getPosition(true),
                        swerveModule[0].getPosition(true),
                        swerveModule[0].getPosition(true),
                        swerveModule[0].getPosition(true)
                }, new Pose2d());
    }

    public Pose2d getPoseFromPhoton() {
        //System.out.print("getPose function called");    
        var result = photonCamera.getLatestResult();
        PhotonTrackedTarget target = result.getBestTarget();

        if (target == null) {
                 System.out.print("target null can't find pose");    
                 return new Pose2d(); 
        }

        var targetPose = target.getBestCameraToTarget();
        // Extract translation and rotation data
        translation = new Translation2d(
                targetPose.getTranslation().getX(),
                targetPose.getTranslation().getY());
        rotation = new Rotation2d(targetPose.getRotation().getAngle());
        System.out.print("target Identified");  
        return new Pose2d(translation, rotation);
    }

    public void UpdatePose(){
        //System.out.print("Update Pose called"); 
        getPoseFromPhoton();
        swervePoseEstimator.addVisionMeasurement(new Pose2d(translation, rotation), 0.1);
       // System.out.print("Pose updating");
    }
    public void PoseReset(){
        translation = new Translation2d();
        rotation = new Rotation2d();
        //System.out.print("Pose has been reset");
    }

    @Override
    public void periodic() {
         // This method will be called once per scheduler run
    }
}

