{"post_stream":{"posts":[{"id":3353674,"name":"","username":"Farquaad","avatar_template":"/user_avatar/www.chiefdelphi.com/farquaad/{size}/218709_2.png","created_at":"2024-04-07T01:22:53.468Z","cooked":"<p>My team is trying to get SwervePoseEstimation working so that we can have more effective autos. But when we implement the SwerveDrivePoseEstimator is gives us an error.</p>
<p>Here is our swerve Subsystem(Please excuse the mess):</p>
<pre><code class="lang-auto">package frc.robot.subsystems;

import frc.robot.SwerveModule;
import frc.robot.Constants;

import edu.wpi.first.math.kinematics.ChassisSpeeds;
import edu.wpi.first.math.kinematics.SwerveDriveKinematics;
import edu.wpi.first.math.kinematics.SwerveDriveOdometry;
import edu.wpi.first.math.kinematics.SwerveModulePosition;

import com.ctre.phoenix6.configs.Pigeon2Configuration;
import com.ctre.phoenix6.hardware.Pigeon2;
import com.pathplanner.lib.auto.AutoBuilder;
import com.pathplanner.lib.util.HolonomicPathFollowerConfig;
import com.pathplanner.lib.util.PIDConstants;
import com.pathplanner.lib.util.ReplanningConfig;

import edu.wpi.first.math.estimator.PoseEstimator;
import edu.wpi.first.math.estimator.SwerveDrivePoseEstimator;
import edu.wpi.first.math.geometry.Pose2d;
import edu.wpi.first.math.geometry.Pose3d;
import edu.wpi.first.math.geometry.Rotation2d;
import edu.wpi.first.math.geometry.Translation2d;
import edu.wpi.first.math.kinematics.SwerveModuleState;
import edu.wpi.first.wpilibj.DriverStation;
import edu.wpi.first.wpilibj.Timer;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj2.command.SubsystemBase;

public class Swerve extends SubsystemBase {
    // public SwerveDriveOdometry swerveOdometry;
    public SwerveModule[] mSwerveMods;
    public Pigeon2 gyro;

    private Vision vision;
    private SwerveDrivePoseEstimator swervePoseEstimator; 

    public Swerve() {
        gyro = new Pigeon2(Constants.Swerve.pigeonID);
        gyro.getConfigurator().apply(new Pigeon2Configuration());
        gyro.setYaw(0);

        mSwerveMods = new SwerveModule[] {
            new SwerveModule(0, Constants.Swerve.Mod0.constants),
            new SwerveModule(1, Constants.Swerve.Mod1.constants),
            new SwerveModule(2, Constants.Swerve.Mod2.constants),
            new SwerveModule(3, Constants.Swerve.Mod3.constants)
        };

        // swerveOdometry = new SwerveDriveOdometry(Constants.Swerve.swerveKinematics, getGyroYaw(), getModulePositions());
        swervePoseEstimator =  new SwerveDrivePoseEstimator(Constants.Swerve.swerveKinematics, getGyroYaw(), getModulePositions(), getPose());
        swervePoseEstimator.addVisionMeasurement(vision.getPose2d(), Timer.getFPGATimestamp());

        AutoBuilder.configureHolonomic(
            this::getEstimatedPose,
            // this::getPose,
             this::zeroPose,
              this::getChassisSpeeds,
               this::setChassisSpeeds,
                new HolonomicPathFollowerConfig(
                    new PIDConstants(4,0,0),  //good numbers
                    new PIDConstants(1,0,0), //good numbers
                     4.5, //this is fine
                      0.39, //this is all fine
                       new ReplanningConfig() 
                    ),
                 () -&gt; {
                    var alliance = DriverStation.getAlliance();
                    if (alliance.isPresent()) {
                        return alliance.get() == DriverStation.Alliance.Red;
                    }
                    return false; 
                 },
                  this 
        );
    }

    public void drive(Translation2d translation, double rotation, boolean fieldRelative, boolean isOpenLoop) {
        SwerveModuleState[] swerveModuleStates =
            Constants.Swerve.swerveKinematics.toSwerveModuleStates(
                fieldRelative ? ChassisSpeeds.fromFieldRelativeSpeeds(
                                    translation.getX(), 
                                    translation.getY(), 
                                    rotation, 
                                    getHeading()
                                    // getGyroYaw()
                                )
                                : new ChassisSpeeds(
                                    translation.getX(), 
                                    translation.getY(), 
                                    rotation
                                    )
                                );
        SwerveDriveKinematics.desaturateWheelSpeeds(swerveModuleStates, Constants.Swerve.maxSpeed);

        for(SwerveModule mod : mSwerveMods){
            mod.setDesiredState(swerveModuleStates[mod.moduleNumber], isOpenLoop);
        }

    }   

    /* Used by SwerveControllerCommand in Auto */
    public void setModuleStates(SwerveModuleState[] desiredStates) {
        SwerveDriveKinematics.desaturateWheelSpeeds(desiredStates, Constants.Swerve.maxSpeed);

        for(SwerveModule mod : mSwerveMods){
            mod.setDesiredState(desiredStates[mod.moduleNumber], false);
        }
    }

    public SwerveModuleState[] getModuleStates(){
        SwerveModuleState[] states = new SwerveModuleState[4];
        for(SwerveModule mod : mSwerveMods){
            states[mod.moduleNumber] = mod.getState();
        }
        return states;
    }

    public SwerveModulePosition[] getModulePositions(){
        SwerveModulePosition[] positions = new SwerveModulePosition[4];
        for(SwerveModule mod : mSwerveMods){
            positions[mod.moduleNumber] = mod.getPosition();
        }
        return positions;
    }

    public Pose2d getPose() {
        // return swerveOdometry.getPoseMeters();
        return swervePoseEstimator.getEstimatedPosition();
    }

    public Pose2d getEstimatedPose() {
        return swervePoseEstimator.getEstimatedPosition();
    }

    public void setPose(Pose2d pose) {
        // swerveOdometry.resetPosition(getGyroYaw(), getModulePositions(), pose);
        swervePoseEstimator.resetPosition(getGyroYaw(), getModulePositions(), pose);
    }

    public void zeroPose(Pose2d pose) {
        // swerveOdometry.resetPosition(getGyroYaw(), getModulePositions(), pose);
        swervePoseEstimator.resetPosition(getGyroYaw(), getModulePositions(), pose);
    }

    public Rotation2d getHeading(){
        return getPose().getRotation();
    }

    public void setHeading(Rotation2d heading){
        // swerveOdometry.resetPosition(getGyroYaw(), getModulePositions(), new Pose2d(getPose().getTranslation(), heading));
        swervePoseEstimator.resetPosition(getGyroYaw(), getModulePositions(), new Pose2d(getPose().getTranslation(), heading));
    }

    public void zeroHeading(){
        // swerveOdometry.resetPosition(getGyroYaw(), getModulePositions(), new Pose2d(getPose().getTranslation(), new Rotation2d()));
        swervePoseEstimator.resetPosition(getGyroYaw(), getModulePositions(), new Pose2d(getPose().getTranslation(), new Rotation2d()));
    }

    public Rotation2d getGyroYaw() {
        return Rotation2d.fromDegrees(gyro.getYaw().getValue());
    }

    public Rotation2d getGyroscopeRotation() {
        return gyro.getRotation2d();
      }

    public void resetModulesToAbsolute(){
        for(SwerveModule mod : mSwerveMods){
            mod.resetToAbsolute();
        }
    }

    public double getGyroDouble() {
        return gyro.getYaw().getValueAsDouble();
    }

    public ChassisSpeeds getChassisSpeeds() {
        SwerveModuleState[] currentModuleState = new SwerveModuleState[this.mSwerveMods.length];
        for (int i = 0; i &lt; this.mSwerveMods.length; i++) {
            currentModuleState[i] = mSwerveMods[i].getState();
        }
        return Constants.Swerve.swerveKinematics.toChassisSpeeds(currentModuleState);
    }

    public void setChassisSpeeds(ChassisSpeeds chassisSpeeds) {
        setModuleStates(Constants.Swerve.swerveKinematics.toSwerveModuleStates(chassisSpeeds));
    }

    public void stopModules() {
        for(SwerveModule mod : mSwerveMods){
            mod.stop();
        }
    }

    @Override
    public void periodic(){
        // swerveOdometry.update(getGyroYaw(), getModulePositions());
        swervePoseEstimator.update(getGyroYaw(), getModulePositions());

        for(SwerveModule mod : mSwerveMods){
            SmartDashboard.putNumber("Mod " + mod.moduleNumber + " CANcoder", mod.getCANcoder().getDegrees());
            SmartDashboard.putNumber("Mod " + mod.moduleNumber + " Angle", mod.getPosition().angle.getDegrees());
            SmartDashboard.putNumber("Mod " + mod.moduleNumber + " Velocity", mod.getState().speedMetersPerSecond);    
        }
    }
}
</code></pre>
<p>And here is the stack trace for the error we are getting:</p>
<pre><code class="lang-auto">Error at frc.robot.subsystems.Swerve.getPose(Swerve.java:129): Unhandled exception: java.lang.NullPointerException: Cannot invoke "edu.wpi.first.math.estimator.SwerveDrivePoseEstimator.getEstimatedPosition()" because "this.swervePoseEstimator" is null
        at frc.robot.subsystems.Swerve.getPose(Swerve.java:129)
        at frc.robot.subsystems.Swerve.&lt;init&gt;(Swerve.java:51)
</code></pre>
<p>Any help would be greatly appreciated!</p>","post_number":1,"post_type":1,"posts_count":5,"updated_at":"2024-04-07T01:22:53.468Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":174,"reads":108,"readers_count":107,"score":891.6,"yours":false,"topic_id":461496,"topic_slug":"swerve-pose-estimation-error","display_username":"","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":null,"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":63302,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/swerve-pose-estimation-error/461496/1","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null,"can_vote":false},{"id":3353692,"name":"Arshan","username":"arshan","avatar_template":"/user_avatar/www.chiefdelphi.com/arshan/{size}/219045_2.png","created_at":"2024-04-07T01:38:32.010Z","cooked":"<p>You are using getPose() when you construct your pose estimator object, which tries to use the pose estimator object except it doesn’t exist yet so it’s null explaining the error message. Rather than calling getPose() just make an empty pose. Feel free to check out my team’s robot <a href="https://github.com/frc-emotion/Crescendo-2024/blob/main/src/main/java/frc/robot/subsystems/VisionSubsystem.java" rel="noopener nofollow ugc">code</a> for further reference.</p>
<p>Before<br>
<code>new SwerveDrivePoseEstimator(Constants.Swerve.swerveKinematics, getGyroYaw(), getModulePositions(), getPose()); </code></p>
<p>After<br>
<code>new SwerveDrivePoseEstimator(Constants.Swerve.swerveKinematics, getGyroYaw(), getModulePositions(), new Pose2d());</code></p>","post_number":2,"post_type":1,"posts_count":5,"updated_at":"2024-04-07T01:41:10.151Z","reply_count":1,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":5,"reads":77,"readers_count":76,"score":75.4,"yours":false,"topic_id":461496,"topic_slug":"swerve-pose-estimation-error","display_username":"Arshan","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"link_counts":[{"url":"https://github.com/frc-emotion/Crescendo-2024/blob/main/src/main/java/frc/robot/subsystems/VisionSubsystem.java","internal":false,"reflection":false,"title":"Crescendo-2024/src/main/java/frc/robot/subsystems/VisionSubsystem.java at main · frc-emotion/Crescendo-2024 · GitHub","clicks":27}],"read":true,"user_title":null,"bookmarked":false,"actions_summary":[{"id":2,"count":2}],"moderator":false,"admin":false,"staff":false,"user_id":64961,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":""},"post_url":"/t/swerve-pose-estimation-error/461496/2","custom_user_title":null,"reactions":[{"id":"heart","type":"emoji","count":2}],"current_user_reaction":null,"reaction_users_count":2,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3353707,"name":"","username":"Farquaad","avatar_template":"/user_avatar/www.chiefdelphi.com/farquaad/{size}/218709_2.png","created_at":"2024-04-07T01:54:32.741Z","cooked":"<p>Thank you so much for the response, that was causing 99% of the issues, and looking at your teams code help me get the last 1% solved.</p>
