// 1: Front Right Wheel
// 2: Back Left Wheel
// 3: Back Right Wheel

x = ((delta[0] - delta[1] + delta[2] - delta[3]) * sqrt(2) / 4.0) * Constants::DISTANCE_PER_TICK;
y = ((delta[0] + delta[1] - delta[2] - delta[3]) * sqrt(2) / 4.0) * Constants::DISTANCE_PER_TICK;

r = std::hypot(x, y);
th = std::atan2(y, x) + current_yaw;

result_x += r * std::cos(th);
result_y += r * std::sin(th);
</code></pre>
