<p>package frc.robot;</p>
<p>import edu.wpi.first.wpilibj.Joystick;<br>
import edu.wpi.first.wpilibj.TimedRobot;<br>
import com.revrobotics.CANSparkMax;<br>
import com.revrobotics.CANSparkMax.IdleMode;<br>
import com.revrobotics.CANSparkMaxLowLevel.MotorType;<br>
import edu.wpi.first.wpilibj.drive.DifferentialDrive;</p>
<p>public class Robot extends TimedRobot {<br>
private CANSparkMax m_rightLeadMotor, m_rightFollowMotor, m_leftLeadMotor, m_leftFollowMotor ;<br>
private final DifferentialDrive m_robotDrive = new DifferentialDrive(m_leftLeadMotor, m_rightLeadMotor);<br>
private Joystick m_joystick;</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void robotInit() {<br>
/**MAKE SURE THAT THE PHYSICAL DEVICES AND DEVICE ID’S AGREE  */</p>
<pre><code>m_leftLeadMotor = new CANSparkMax(1, MotorType.kBrushless);
m_leftFollowMotor = new CANSparkMax(2, MotorType.kBrushless);       
m_rightLeadMotor = new CANSparkMax(3, MotorType.kBrushless);
m_rightFollowMotor = new CANSparkMax(4, MotorType.kBrushless);

m_rightLeadMotor.setInverted(true);

m_leftLeadMotor.restoreFactoryDefaults();
m_leftFollowMotor.restoreFactoryDefaults(); 
m_rightLeadMotor.restoreFactoryDefaults();
m_rightFollowMotor.restoreFactoryDefaults();

m_leftLeadMotor.setIdleMode(IdleMode.kBrake);
m_leftFollowMotor.setIdleMode(IdleMode.kBrake);
m_rightLeadMotor.setIdleMode(IdleMode.kBrake);
m_rightFollowMotor.setIdleMode(IdleMode.kBrake);

m_rightFollowMotor.follow(m_rightLeadMotor);
m_leftFollowMotor.follow(m_leftLeadMotor); 
m_joystick = new Joystick(0);
</code></pre>
<p>}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void robotPeriodic() {}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void autonomousInit() {}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void autonomousPeriodic() {}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void teleopInit() {}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void teleopPeriodic() {<br>
m_robotDrive.arcadeDrive(-m_joystick.getY(), -m_joystick.getX());<br>
}</p>
<p><a class="mention" href="/u/override">@Override</a><br>
public void disabledInit() {}</p>
