<p><a class="mention" href="/u/override">@Override</a><br>
public void simulationPeriodic() {}<br>
}</p>","post_number":4,"post_type":1,"posts_count":6,"updated_at":"2023-02-17T18:13:40.359Z","reply_count":1,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":5,"reads":81,"readers_count":80,"score":46.2,"yours":false,"topic_id":427142,"topic_slug":"new-to-neo-drive-train-what-is-best-with-java-motor-group-or-leader-follower","display_username":"Bill Bagshaw","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":50591,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":""},"post_url":"/t/new-to-neo-drive-train-what-is-best-with-java-motor-group-or-leader-follower/427142/4","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3147603,"name":"Allen Nuttle","username":"nuttle","avatar_template":"/user_avatar/www.chiefdelphi.com/nuttle/{size}/124105_2.png","created_at":"2023-02-17T18:51:43.720Z","cooked":"<p>This looks to be on the right track.</p>
