<pre><code>clawCheckTimer++;
if(xb.getRightBumperPressed() == true &amp;&amp; clawCheck == false &amp;&amp; clawCheckTimer &gt; 25) {
  clawCheck = true;
  clawCheckTimer = 0;
}else if(xb.getRightBumperPressed() == true &amp;&amp; clawCheck == true &amp;&amp; clawCheckTimer &gt; 25) {
  clawCheck = false;
  clawCheckTimer = 0;
}
if(clawCheck == true) {
  clawSolenoid.set(Value.kForward);
}else {
  clawSolenoid.set(Value.kReverse);
}
