<pre><code class="lang-auto">public class AbsoluteAnalogEncoder extends AnalogInput {

    private double offset; //Declare offset variable
    private boolean reversed; //Declare reversed

    public AbsoluteAnalogEncoder(int port, double offset, boolean reversed) {
        super(port); 
        setOffset(offset);
        this.reversed = reversed;
    }

    private void setOffset(double offset) {
        this.offset = (offset / 360 * RobotController.getVoltage5V()) % RobotController.getVoltage5V();
        if (this.offset &lt; 0)
            this.offset += RobotController.getVoltage5V();
    }

    @Override
    public double getVoltage() {
        double returnValue = super.getVoltage();
        if (this.reversed) returnValue *= -1;
        returnValue = (returnValue - this.offset) % RobotController.getVoltage5V(); 
        if (returnValue &lt; 0)
            returnValue += RobotController.getVoltage5V();
        return returnValue;

    }

    public double getRotationPercent() {
        return (getVoltage() / RobotController.getVoltage5V());
    }

    public double getRotationDegrees() {
        return getRotationPercent() * 360.0;
    }

}
</code></pre>","post_number":7,"post_type":1,"posts_count":8,"updated_at":"2023-02-10T01:37:38.201Z","reply_count":0,"reply_to_post_number":5,"quote_count":0,"incoming_link_count":0,"reads":22,"readers_count":21,"score":4.4,"yours":false,"topic_id":426299,"topic_slug":"ma3-absolute-encoders-constantly-reporting-360","display_username":"","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":65,"username":"Joe_Ross","name":"Joe Ross","avatar_template":"/user_avatar/www.chiefdelphi.com/joe_ross/{size}/37_2.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":52621,"hidden":false,"trust_level":1,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/ma3-absolute-encoders-constantly-reporting-360/426299/7","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":true},{"id":3306122,"name":"system","username":"system","avatar_template":"/uploads/default/original/4X/9/3/2/93234578e69f55873c63fc00fe2d0d85f23bce55.png","created_at":"2024-02-10T01:37:40.095Z","cooked":"<p>This topic was automatically closed 365 days after the last reply. New replies are no longer allowed.</p>","post_number":8,"post_type":3,"posts_count":8,"updated_at":"2024-02-10T01:37:40.095Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":0,"reads":3,"readers_count":2,"score":0.6,"yours":false,"topic_id":426299,"topic_slug":"ma3-absolute-encoders-constantly-reporting-360","display_username":"system","primary_group_name":null,"flair_name":null