public Command getAutonomousCommand(){

    Pose2d start = new Pose2d(0,0,new Rotation2d(0));
    List&lt;Translation2d&gt; waypoints = List.of(
      new Translation2d(1,.0),
      new Translation2d(1,0)

    );
    Pose2d end = new Pose2d(3, 0, Rotation2d.fromDegrees(0));
    return this.createAutoNavigationCommand(start, waypoints, end);
}

public Command createAutoNavigationCommand(Pose2d start, List&lt;Translation2d&gt; waypoints, Pose2d end) {
    System.out.println("Creating Auto Command");
    var autoVoltageConstraint = new DifferentialDriveVoltageConstraint(
        new SimpleMotorFeedforward(0.699, 0.694, 0.0993),
        drive.getKinematics(), 10);

    // Create config for trajectory
    TrajectoryConfig config = new TrajectoryConfig(5, .25)
        // Add kinematics to ensure max speed is actually obeyed
        .setKinematics(drive.getKinematics())
        // Apply the voltage constraint
        .addConstraint(autoVoltageConstraint);

    // An example trajectory to follow. All units in meters.
    Trajectory trajectory = TrajectoryGenerator.generateTrajectory(start, waypoints, end, config);
    System.out.println("Generated Trajectory");
    RamseteCommand ramseteCommand = new RamseteCommand(
      trajectory, 
      drive::getPose,
        new RamseteController(2, 0.7),
        drive.getKinematics(),
        drive::tankDriveVelocity, drive);

    // Run path following command, then stop at the end.
    System.out.println("Finished Creating Auto Command");
    return ramseteCommand.andThen(() -&gt; drive.tankDriveVolts(0, 0));
  }

public void tankDriveVelocity(double leftVel, double rightVel){
        System.out.println(leftVel + ","+ rightVel);  

        double leftLeaderNativeVelocity =  10 * (1 / (Constants.DRIVE_METERS_PER_COUNT)*leftVel);
        double rightLeaderNativeVelocity = 10 * (1 / (Constants.DRIVE_METERS_PER_COUNT)*rightVel);

        frontLeft.set(ControlMode.Velocity, leftLeaderNativeVelocity);
        frontRight.set(ControlMode.Velocity, rightLeaderNativeVelocity);


      }
