<pre><code>    if(currentTime &gt; 0.250 &amp;&amp; Limelight.valid()){
        double dL = Limelight.getDistance()*0.0254;
        double tR = m_drive.getGyro().getRadians();
        double tT = m_turret.getMeasurement()-Math.PI;
        double tL = -1.0*Limelight.tx();

        Pose2d pose = calcPoseFromVision(dL, tR, tT, tL, GoalConstants.kGoalLocation);

        if(m_updatePose){
            m_drive.setPose(pose);
     }

private Pose2d calcPoseFromVision(double dL, double tR, double tT, double tL, Translation2d goal){
            double tG = tR+tT+tL;
            double rX = goal.getX()-dL*Math.cos(tG);
            double rY = goal.getY()-dL*Math.sin(tG);

            return new Pose2d(rX,rY, new Rotation2d(-tR));
}</code></pre>","post_number":5,"post_type":1,"posts_count":6,"updated_at":"2022-04-27T22:55:46.874Z","reply_count":0,"reply_to_post_number":3,"quote_count":0,"incoming_link_count":18,"reads":217,"readers_count":216,"score":178.4,"yours":false,"topic_id":410382,"topic_slug":"resetting-odometry-with-limelight","display_username":"Emerson OHara","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":45763,"username":"Starlight220","name":null,"avatar_template":"/user_avatar/www.chiefdelphi.com/starlight220/{size}/150872_2.png"},"bookmarked":false,"actions_summary":[{"id":2,"count":3}],"moderator":false,"admin":false,"staff":false,"user_id":43759,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"1706 Drive Coach | 9401 Mentor | #TeamThrifty"},"post_url":"/t/resetting-odometry-with-limelight/410382/5","custom_user_title":"1706 Drive Coach | 9401 Mentor | #TeamThrifty","reactions":[{"id":"heart","type":"emoji","count":3}],"current_user_reaction":null,"reaction_users_count":3,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3200752,"name":"system","username":"system","avatar_template":"/uploads/default/original/4X/9/3/2/93234578e69f55873c63fc00fe2d0d85f23bce55.png","created_at":"2023-04-27T22:56:14.568Z","cooked":"<p>This topic was automatically closed 365 days after the last reply. New replies are no longer allowed.</p>","post_number":6,"post_type":3,"posts_count":6,"updated_at":"2023-04-27T22:56:14.568Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":1,"reads":18,"reade