package frc.robot;

import com.ctre.phoenix.motorcontrol.FeedbackDevice;

import com.ctre.phoenix.motorcontrol.FollowerType;

import com.ctre.phoenix.motorcontrol.NeutralMode;

import com.ctre.phoenix.motorcontrol.StatusFrameEnhanced;

import com.ctre.phoenix.motorcontrol.TalonFXControlMode;

import com.ctre.phoenix.motorcontrol.TalonFXInvertType;

import com.ctre.phoenix.motorcontrol.can.TalonFX;

import edu.wpi.first.wpilibj.Joystick;

import edu.wpi.first.wpilibj.TimedRobot;

import edu.wpi.first.wpilibj.smartdashboard.SendableChooser;

import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

/**

 * The VM is configured to automatically run this class, and to call the functions corresponding to

 * each mode, as described in the TimedRobot documentation. If you change the name of this class or

 * the package after creating this project, you must also update the build.gradle file in the

 * project.

 */

public class Robot extends TimedRobot {

  private static final String kDefaultAuto = "Default";

  private static final String kCustomAuto = "My Auto";

  private String m_autoSelected;

  private final SendableChooser&lt;String&gt; m_chooser = new SendableChooser&lt;&gt;();

  private Joystick joystick = new Joystick(0);



  TalonFX leftMaster = new TalonFX(1);

  TalonFX leftSlave = new TalonFX(2);

  TalonFX rightMaster = new TalonFX(3);

  TalonFX rightSlave = new TalonFX(4);

  int _smoothing = 0;

  int _pov = -1;

  /**

   * This function is run when the robot is first started up and should be used for any

   * initialization code.

   */

  @Override

  public void robotInit() {

    m_chooser.setDefaultOption("Default Auto", kDefaultAuto);

    m_chooser.addOption("My Auto", kCustomAuto);

    SmartDashboard.putData("Auto choices", m_chooser);

    leftMaster .configFactoryDefault();

    leftSlave  .configFactoryDefault();

    rightMaster.configFactoryDefault();

    rightSlave .configFactoryDefault();

    leftMaster .configSelectedFeedbackSensor(FeedbackDevice.IntegratedSensor, 0,30);

    leftSlave  .configSelectedFeedbackSensor(FeedbackDevice.IntegratedSensor, 0,30);

    rightMaster.configSelectedFeedbackSensor(FeedbackDevice.IntegratedSensor, 0,30);

    rightSlave .configSelectedFeedbackSensor(FeedbackDevice.IntegratedSensor, 0,30);

    leftMaster.setNeutralMode(NeutralMode.Coast);

    rightMaster.setNeutralMode(NeutralMode.Coast);

    leftSlave.setNeutralMode(NeutralMode.Coast);

    rightSlave.setNeutralMode(NeutralMode.Coast);



    leftMaster.setSensorPhase(false);

    leftMaster.setInverted(TalonFXInvertType.CounterClockwise);

    rightMaster.setSensorPhase(true);

    rightMaster.setInverted(TalonFXInvertType.Clockwise);

    leftMaster.setStatusFramePeriod(StatusFrameEnhanced.Status_13_Base_PIDF0, 10, 30);

    leftMaster.setStatusFramePeriod(StatusFrameEnhanced.Status_10_MotionMagic, 10, 30);

    rightMaster.setStatusFramePeriod(StatusFrameEnhanced.Status_13_Base_PIDF0, 10, 30);

    rightMaster.setStatusFramePeriod(StatusFrameEnhanced.Status_10_MotionMagic, 10, 30);

    /* Set the peak and nominal outputs */

    leftMaster.configNominalOutputForward(0, 30);

    leftMaster.configNominalOutputReverse(0, 30);

    leftMaster.configPeakOutputForward(1, 30);

    leftMaster.configPeakOutputReverse(-1, 30);

    rightMaster.configNominalOutputForward(0, 30);

    rightMaster.configNominalOutputReverse(0, 30);

    rightMaster.configPeakOutputForward(1, 30);

    rightMaster.configPeakOutputReverse(-1, 30);



    leftMaster.configNeutralDeadband(0.001, 30);

    rightMaster.configNeutralDeadband(0.001, 30);

    /* Set Motion Magic gains in slot0 - see documentation */

    leftMaster.selectProfileSlot(0, 0);

    leftMaster.config_kF(0, 0.04, 30);//KF

    leftMaster.config_kP(0, 0.2 , 30); //KP

    leftMaster.config_kI(0, 0, 30);   //KI

    leftMaster.config_kD(0, 0, 30);   //KD

    rightMaster.selectProfileSlot(0, 0);

    rightMaster.config_kF(0, 0.04, 30);//KF

    rightMaster.config_kP(0, 0.2, 30); //KP

    rightMaster.config_kI(0, 0, 30);   //KI

    rightMaster.config_kD(0, 0, 30);   //KD

    leftMaster.setSelectedSensorPosition(0, 0, 30);

    rightMaster.setSelectedSensorPosition(0, 0, 30);

    /*

    leftSlave.follow(leftMaster, FollowerType.PercentOutput);

    rightSlave.follow(rightMaster, FollowerType.PercentOutput);

    rightSlave.setInverted(TalonFXInvertType.FollowMaster);

    leftSlave.setInverted(TalonFXInvertType.FollowMaster);

*/

  }

  /**

   * This function is called every 20 ms, no matter the mode. Use this for items like diagnostics

   * that you want ran during disabled, autonomous, teleoperated and test.

   *

   * &lt;p&gt;This runs after the mode specific periodic functions, but before LiveWindow and

   * SmartDashboard integrated updating.

   */

  @Override

  public void robotPeriodic() {



    SmartDashboard.putNumber("Left Encoder counts", leftMaster.getSelectedSensorPosition());

    SmartDashboard.putNumber("Right Encoder counts", rightMaster.getSelectedSensorPosition());

  }

  /**

   * This autonomous (along with the chooser code above) shows how to select between different

   * autonomous modes using the dashboard. The sendable chooser code works with the Java

   * SmartDashboard. If you prefer the LabVIEW Dashboard, remove all of the chooser code and

   * uncomment the getString line to get the auto name from the text box below the Gyro

   *

   * &lt;p&gt;You can add additional auto modes by adding additional comparisons to the switch structure

   * below with additional strings. If using the SendableChooser make sure to add them to the

   * chooser code above as well.

   */

  @Override

  public void autonomousInit() {

    m_autoSelected = m_chooser.getSelected();

    // m_autoSelected = SmartDashboard.getString("Auto Selector", kDefaultAuto);

    System.out.println("Auto selected: " + m_autoSelected);

  }

  /** This function is called periodically during autonomous. */

  @Override

  public void autonomousPeriodic() {

    switch (m_autoSelected) {

      case kCustomAuto:

        // Put custom auto code here

        break;

      case kDefaultAuto:

      default:

        // Put default auto code here

        break;

    }

  }

  /** This function is called once when teleop is enabled. */

  @Override

  public void teleopInit() {}

  /** This function is called periodically during operator control. */

  @Override

  public void teleopPeriodic() {

    double gamepadYAxis = Math.abs(joystick.getRawAxis(1)) &gt; 0.2 ? joystick.getRawAxis(1): 0;

    double gamepadRYAxis = Math.abs(joystick.getRawAxis(5)) &gt; 0.2 ? joystick.getRawAxis(1): 0;

    //double targetPos = gamepadRYAxis * 2048 * 10.0; ///(.1016 * Math.PI));

    double targetPos =-1 * ((2048 * 7.0 )/(.1016 * Math.PI));

    SmartDashboard.putNumber("TaretPos", targetPos);

    if (joystick.getRawButton(1)) {

      /* Motion Magic */

      /* 2048 ticks/rev * 10 Rotations in either direction */

      leftMaster.set(TalonFXControlMode.MotionMagic, targetPos);

      rightMaster.set(TalonFXControlMode.MotionMagic, targetPos);

    }else if (joystick.getRawButton(2)) {

      /* Motion Magic */

      /* 2048 ticks/rev * 10 Rotations in either direction */

      leftMaster.set(TalonFXControlMode.MotionMagic, targetPos);

      rightMaster.set(TalonFXControlMode.MotionMagic, targetPos);

    } else {

      /* Percent Output */

      leftMaster.set(TalonFXControlMode.PercentOutput, -gamepadYAxis);

      leftSlave.set(TalonFXControlMode.PercentOutput, -gamepadYAxis);

      rightMaster.set(TalonFXControlMode.PercentOutput, -gamepadYAxis);

      rightSlave.set(TalonFXControlMode.PercentOutput, gamepadYAxis);



    }

    if (joystick.getRawButton(3)) {

      /* Zero sensor positions */

      leftMaster.setSelectedSensorPosition(0);

      rightMaster.setSelectedSensorPosition(0);

    }

    int pov = joystick.getPOV();

    if (_pov == pov) {

      /* no change */

    } else if (_pov == 180) { // D-Pad down

      /* Decrease smoothing */

      _smoothing--;

      if (_smoothing &lt; 0)

        _smoothing = 0;

      leftMaster.configMotionSCurveStrength(_smoothing);

      rightMaster.configMotionSCurveStrength(_smoothing);

      System.out.println("Smoothing is set to: " + _smoothing);

    } else if (_pov == 0) { // D-Pad up

      /* Increase smoothing */

      _smoothing++;

      if (_smoothing &gt; 8)

        _smoothing = 8;

      leftMaster.configMotionSCurveStrength(_smoothing);

      rightMaster.configMotionSCurveStrength(_smoothing);

    }

    _pov = pov; /* save the pov value for next time */

    SmartDashboard.putNumber("Smoothing", _smoothing);

  }

  /** This function is called once when the robot is disabled. */

  @Override

  public void disabledInit() {}

  /** This function is called periodically when disabled. */

  @Override

  public void disabledPeriodic() {}

  /** This function is called once when test mode is enabled. */

  @Override

  public void testInit() {}

  /** This function is called periodically during test mode. */

  @Override

  public void testPeriodic() {}

  /** This function is called once when the robot is first started up. */

  @Override

  public void simulationInit() {}

  /** This function is called periodically whilst in simulation. */

  @Override

  public void simulationPeriodic() {}

