imates, make sure that 3D processing is enabled in the <span class="abbreviation">PV<template class="tooltiptext" data-text="Photon Vision, an open source vision library for coprocessors"></template></span> UI. With this still set to 2D, you’d see tags, but never get pose estimates.</p>","post_number":4,"post_type":1,"posts_count":19,"updated_at":"2025-05-25T16:44:51.656Z","reply_count":0,"reply_to_post_number":null,"quote_count":0,"incoming_link_count":0,"reads":101,"readers_count":100,"score":35.2,"yours":false,"topic_id":502371,"topic_slug":"help-needed-no-pose-output-from-photonposeestimator-in-photonlib-2025-3-1","display_username":"Nolan Barker","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","bookmarked":false,"actions_summary":[{"id":2,"count":1}],"moderator":false,"admin":false,"staff":false,"user_id":65568,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"Programming Lead, Co-captain | 3414 Hackbots"},"post_url":"/t/help-needed-no-pose-output-from-photonposeestimator-in-photonlib-2025-3-1/502371/4","custom_user_title":"Programming Lead, Co-captain | 3414 Hackbots","reactions":[{"id":"heart","type":"emoji","count":1}],"current_user_reaction":null,"reaction_users_count":1,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3635529,"name":"Tio","username":"Tio","avatar_template":"/letter_avatar_proxy/v4/letter/t/d2c977/{size}.png","created_at":"2025-05-25T16:57:16.204Z","cooked":"<p>I have the latest PV20025.3.1</p>","post_number":5,"post_type":1,"posts_count":19,"updated_at":"2025-05-25T16:57:16.204Z","reply_count":0,"reply_to_post_number":3,"quote_count":0,"incoming_link_count":0,"reads":96,"readers_count":95,"score":19.2,"yours":false,"topic_id":502371,"topic_slug":"help-needed-no-pose-output-from-photonposeestimator-in-photonlib-2025-3-1","display_username":"Tio","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":65568,"username":"therekrab","name":"Nolan Barker","avatar_template":"/user_avatar/www.chiefdelphi.com/therekrab/{size}/248500_2.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":28847,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"post_url":"/t/help-needed-no-pose-output-from-photonposeestimator-in-photonlib-2025-3-1/502371/5","custom_user_title":null,"reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3635530,"name":"","username":"LPryor","avatar_template":"/user_avatar/www.chiefdelphi.com/lpryor/{size}/220287_2.png","created_at":"2025-05-25T17:01:21.074Z","cooked":"<ul>
