import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;<br>
import edu.wpi.first.wpilibj2.command.SubsystemBase;</p>
<p>import org.photonvision.EstimatedRobotPose;<br>
import org.photonvision.PhotonCamera;<br>
import org.photonvision.PhotonPoseEstimator;<br>
import org.photonvision.PhotonPoseEstimator.PoseStrategy;<br>
import org.photonvision.targeting.PhotonPipelineResult;</p>
<p>import java.util.Optional;</p>
<p>public class VisionSubsystemFullField extends SubsystemBase {</p>
<pre><code>private frc.robot.subsystems.CommandSwerveDrivetrain drivetrain;

public void setDrivetrain(frc.robot.subsystems.CommandSwerveDrivetrain drivetrain) {
    this.drivetrain = drivetrain;
}

private final PhotonCamera leftCam = new PhotonCamera("LeftCam");
private final PhotonCamera rightCam = new PhotonCamera("RightCam");

private final PhotonPoseEstimator leftPoseEstimator;
private final PhotonPoseEstimator rightPoseEstimator;

private final AprilTagFieldLayout fieldLayout;
private Optional&lt;EstimatedRobotPose&gt; latestPose = Optional.empty();

public VisionSubsystemFullField(AprilTagFieldLayout fieldLayout) {
    this.fieldLayout = fieldLayout;

    // Simplified transform for testing
    Transform3d leftCameraToRobot = new Transform3d(
        new Translation3d(0.0, 0.3, 0.3), // 30cm to the left, 30cm up
        new Rotation3d(Math.toRadians(-15), 0, 0) // Pitch down 15 degrees
    );

    Transform3d rightCameraToRobot = new Transform3d(
        new Translation3d(0.0, -0.3, 0.3), // 30cm to the right, 30cm up
        new Rotation3d(Math.toRadians(-15), 0, 0)
    );

    leftPoseEstimator = new PhotonPoseEstimator(
        fieldLayout,
        PoseStrategy.MULTI_TAG_PNP_ON_COPROCESSOR,
        leftCameraToRobot
    );




    rightPoseEstimator = new PhotonPoseEstimator(
        fieldLayout,
        PoseStrategy.MULTI_TAG_PNP_ON_COPROCESSOR,
        rightCameraToRobot
    );



}

@Override
public void periodic() {
    PhotonPipelineResult leftResult = leftCam.getLatestResult();
    PhotonPipelineResult rightResult = rightCam.getLatestResult();

    Optional&lt;EstimatedRobotPose&gt; left = leftPoseEstimator.update(leftResult);
    Optional&lt;EstimatedRobotPose&gt; right = rightPoseEstimator.update(rightResult);

    left.ifPresent(p -&gt; {
SmartDashboard.putNumber("LEFT X", p.estimatedPose.getX());
SmartDashboard.putNumber("LEFT Y", p.estimatedPose.getY());
SmartDashboard.putNumber("LEFT Z", p.estimatedPose.getZ());
SmartDashboard.putNumber("LEFT Yaw (deg)", Math.toDegrees(p.estimatedPose.getRotation().getZ()));
SmartDashboard.putNumber("LEFT Pitch (deg)", Math.toDegrees(p.estimatedPose.getRotation().getY()));
SmartDashboard.putNumber("LEFT Roll (deg)", Math.toDegrees(p.estimatedPose.getRotation().getX()));
</code></pre>
<p>});<br>
right.ifPresent(p → {<br>
SmartDashboard.putNumber(“RIGHT X”, p.estimatedPose.getX());<br>
SmartDashboard.putNumber(“RIGHT Y”, p.estimatedPose.getY());<br>
SmartDashboard.putNumber(“RIGHT Z”, p.estimatedPose.getZ());<br>
SmartDashboard.putNumber(“RIGHT Yaw (deg)”, Math.toDegrees(p.estimatedPose.getRotation().getZ()));<br>
SmartDashboard.putNumber(“RIGHT Pitch (deg)”, Math.toDegrees(p.estimatedPose.getRotation().getY()));<br>
SmartDashboard.putNumber(“RIGHT Roll (deg)”, Math.toDegrees(p.estimatedPose.getRotation().getX()));<br>
});</p>
<pre><code>    if (left.isPresent()) {
        latestPose = left;
    } else if (right.isPresent()) {
        latestPose = right;
    } else {
        latestPose = Optional.empty();
    }

    latestPose.ifPresent(p -&gt; {
// Feed vision pose into drivetrain odometry if drivetrain is connected
if (drivetrain != null) {
    drivetrain.addVisionMeasurement(
        p.estimatedPose.toPose2d(),
        p.timestampSeconds
    );
}
SmartDashboard.putNumber("Vision X", p.estimatedPose.getX());
SmartDashboard.putNumber("Vision Y", p.estimatedPose.getY());
SmartDashboard.putNumber("Vision Z", p.estimatedPose.getZ());
SmartDashboard.putNumber("Vision Yaw (deg)", Math.toDegrees(p.estimatedPose.getRotation().getZ()));
SmartDashboard.putNumber("Vision Pitch (deg)", Math.toDegrees(p.estimatedPose.getRotation().getY()));
SmartDashboard.putNumber("Vision Roll (deg)", Math.toDegrees(p.estimatedPose.getRotation().getX()));
</code></pre>
<p>});</p>
<pre><code>    SmartDashboard.putBoolean("LeftCam Has Targets", leftResult.hasTargets());
    SmartDashboard.putNumber("LeftCam Tag Count", leftResult.getTargets().size());
    SmartDashboard.putNumber("LeftCam Best Tag ID", leftResult.hasTargets() ? leftResult.getBestTarget().getFiducialId() : -1);

    SmartDashboard.putBoolean("RightCam Has Targets", rightResult.hasTargets());
    SmartDashboard.putNumber("RightCam Tag Count", rightResult.getTargets().size());
    SmartDashboard.putNumber("RightCam Best Tag ID", rightResult.hasTargets() ? rightResult.getBestTarget().getFiducialId() : -1);



}

public Optional&lt;EstimatedRobotPose&gt; getLatestPose() {
    return latestPose;
}

public Optional&lt;Pose3d&gt; getLatestRobotPose3d() {
    return latestPose.map(pose -&gt; pose.estimatedPose);
}

public Optional&lt;Pose3d&gt; getTagPose(int tagId) {
    return fieldLayout.getTagPose(tagId);
}

public AprilTagFieldLayout getFieldLayout() {
    return fieldLayout;
}
