  pinMode(SERVO_PIN, INPUT_PULLUP);
  delay(2);
  flushMySerial();

  Serial.println("Normal mode sequence complete.");
}

// Reads and discards any bytes waiting in the SoftwareSerial buffer.
void flushMySerial() {
  while (mySerial.available()) {
    mySerial.read();
  }
}
