<p>SwerveSubsystem.java</p>
<pre data-code-wrap="java"><code class="lang-java"> public SwerveSubsystem(File directory) {
    SwerveDriveTelemetry.verbosity = TelemetryVerbosity.HIGH;
    try {
      swerveDrive = new SwerveParser(directory).createSwerveDrive(SwerveConstants.maxSpeed,
          new Pose2d(new Translation2d(Meter.of(1),
              Meter.of(4)),
              Rotation2d.fromDegrees(0)));
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
    swerveDrive.setHeadingCorrection(false);
    swerveDrive.setCosineCompensator(true); // DISABLE IF YOU ARE IN SIMULATION.
    swerveDrive.setAngularVelocityCompensation(true,
        true,
        0.1);

    setupPathPlanner();
  }
</code></pre>
