<p>PhotonGPCamera.java:</p>
<pre><code class="lang-auto">package frc.robot.subsystems.Vision.VisionCores.GamepieceLocator;

import edu.wpi.first.math.geometry.Transform3d;
import edu.wpi.first.math.geometry.Translation2d;
import edu.wpi.first.math.util.Units;
import edu.wpi.first.wpilibj2.command.SubsystemBase;
import org.photonvision.PhotonCamera;
import org.photonvision.targeting.PhotonPipelineResult;
import org.photonvision.targeting.PhotonTrackedTarget;

import java.util.LinkedList;
import java.util.List;

public class PhotonGPCamera extends SubsystemBase {

    PhotonCamera cam;

    Transform3d offset = new Transform3d();



    public PhotonGPCamera(String name) {
        cam = new PhotonCamera(name);


    }

    public PhotonGPCamera(String name, Transform3d offset) {
        this(name);
        this.offset = offset;
    }

    @Override
    public void periodic() {
//        cam
    }


    public void setCamOffsets(Transform3d t) {
        offset = t;
    }

    public List&lt;Translation2d&gt; getAllGamepiecesOLD() {
        PhotonPipelineResult lastResult = cam.getLatestResult();

        List&lt;PhotonTrackedTarget&gt; gamepieceData = lastResult.targets;


        List&lt;Translation2d&gt; gpList = new LinkedList&lt;&gt;();

        for(PhotonTrackedTarget t : gamepieceData) {
            double targYaw = Units.degreesToRadians(t.getYaw());//+noteCameraOffset.getRotation().getX();
            double targPitch = Units.degreesToRadians(t.getPitch());//+noteCameraOffset.getRotation().getY();

            double noteDistY = (1/Math.tan(targPitch-offset.getRotation().getY()))* offset.getZ();
            double noteDistX = Math.tan(targYaw-offset.getRotation().getZ())*noteDistY;



            gpList.add(new Translation2d(noteDistX+offset.getX(), noteDistY+ offset.getY()));
        }



        return gpList;
    }

    public List&lt;Translation2d&gt; getAllGamepiecesWPI() {
        PhotonPipelineResult lastResult = cam.getLatestResult();

        List&lt;PhotonTrackedTarget&gt; gamepieceData = lastResult.targets;


        List&lt;Translation2d&gt; gpList = new LinkedList&lt;&gt;();

        for(PhotonTrackedTarget t : gamepieceData) {

            double targYaw = Units.degreesToRadians(t.getYaw());//+noteCameraOffset.getRotation().getX();
            double targPitch = Units.degreesToRadians(t.getPitch());//+noteCameraOffset.getRotation().getY();

            double noteDistY = (1/Math.tan(targPitch-offset.getRotation().getY()))* offset.getZ();
            double noteDistX = Math.tan(targYaw-offset.getRotation().getZ())*noteDistY;



            gpList.add(new Translation2d(noteDistX, noteDistY));
        }



        return gpList;
    }


}
</code></pre>
<p>NoteHunter.java :</p>
<pre><code class="lang-auto">package frc.robot.commands.Drivetrain.Auto.Notes;

import edu.wpi.first.math.controller.PIDController;
import edu.wpi.first.math.geometry.Pose2d;
import edu.wpi.first.math.geometry.Translation2d;
import edu.wpi.first.math.kinematics.ChassisSpeeds;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj2.command.Command;
import frc.robot.SubManager;
import frc.robot.subsystems.Drivetrain.SwerveDriveController;
import frc.robot.subsystems.Intake.IntakeSubsystem;
import frc.robot.subsystems.Shooter.ShooterSubsystem;

import java.util.Objects;

public class NoteHunter extends Command {

    SwerveDriveController dt = SubManager.get_drivetrain();

    PIDController rotController = new PIDController(6,0,0);
    PIDController distController = new PIDController(2,0,0);


    public NoteHunter() {

        rotController.enableContinuousInput(-Math.PI,Math.PI);

        addRequirements(dt);

    }

    @Override
    public void initialize() {
//        rotController.
    }


    @Override
    public void execute() {
        Pose2d nearestNote = SubManager.get_fieldGPS().getNearestNote();

        SmartDashboard.putString("nearestNote", nearestNote.toString());
        SmartDashboard.putNumber("nearestNoteDist", nearestNote.getTranslation().getNorm());
        double desiredRot;
        double desiredApproach;

        if(Objects.equals(nearestNote.getTranslation(), new Translation2d())) {
            desiredRot = 5;
            desiredApproach = 0;
        } else {
            desiredRot = rotController.calculate(
                    nearestNote.getRotation().getRadians(),
                    Math.PI/2
            );
//            desiredApproach = 0.0;
            desiredApproach = -distController.calculate(
                    nearestNote.getTranslation().getNorm(),
                    0.0
            );
        }

        dt.setRRSpeeds(
                new ChassisSpeeds(
                        desiredApproach,
                        0.0,
                        desiredRot
                )
        );



    }


    @Override
    public boolean isFinished() {
        return false;
    }

    @Override
    public void end(boolean interrupted) {
        dt.setRRSpeeds(new ChassisSpeeds());
    }


}
