 rev::SparkRelativeEncoder m_intakeEncoder = m_intake.GetEncoder(rev::SparkRelativeEncoder::Type::kHallSensor);
</code></pre>","post_number":7,"post_type":1,"posts_count":10,"updated_at":"2024-01-18T05:01:35.233Z","reply_count":2,"reply_to_post_number":6,"quote_count":0,"incoming_link_count":2,"reads":140,"readers_count":139,"score":48.0,"yours":false,"topic_id":450389,"topic_slug":"using-neo-vortex-integrated-encoder","display_username":"Will Toth","primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_color":null,"flair_color":null,"flair_group_id":null,"badges_granted":[],"version":1,"can_edit":false,"can_delete":false,"can_recover":false,"can_see_hidden_post":true,"can_wiki":false,"read":true,"user_title":"","reply_to_user":{"id":61289,"username":"heightsbytes","name":null,"avatar_template":"/letter_avatar_proxy/v4/letter/h/ac91a4/{size}.png"},"bookmarked":false,"actions_summary":[],"moderator":false,"admin":false,"staff":false,"user_id":6349,"hidden":false,"trust_level":2,"deleted_at":null,"user_deleted":false,"edit_reason":null,"can_view_edit_history":true,"wiki":false,"user_custom_fields":{"user_field_4":"FRC 3005 Mentor"},"post_url":"/t/using-neo-vortex-integrated-encoder/450389/7","custom_user_title":"FRC 3005 Mentor","reactions":[],"current_user_reaction":null,"reaction_users_count":0,"current_user_used_main_reaction":false,"can_accept_answer":false,"can_unaccept_answer":false,"accepted_answer":false,"topic_accepted_answer":null},{"id":3288335,"name":null,"username":"heightsbytes","avatar_template":"/letter_avatar_proxy/v4/letter/h/ac91a4/{size}.png","created_at":"2024-01-18T22:08:23.060Z","cooked":"<p>I’ll be able to test tomorrow. Thanks again for the help.</p>","post_number":8,"post_type":1,"posts_count":10,"updated_at":"2024-01-18T22:08:23.060Z","reply_count":0,"reply_to_post_number":7,"quote_count":0,"incoming_link_count":0,"reads":96,"readers_count":95,"score":19.2,"yours":false,"topic_id":450389,"topic_slug":"using-neo-vortex-integrated-encoder","display_username":null,"primary_group_name":null,"flair_name":null,"flair_url":null,"flair_bg_colo