    m_controller.setGoal(swivTargetPosition);
}

  @Override
  public void periodic() {
    SmartDashboard.putNumber("SwivelEnc", getEncoder()); // For testing
    // This method will be called once per scheduler run
    setOutput(calculate());
  }
}
